            --color-hyperbola: #f0a060;
            --color-radius: var(--accent-color);
            --color-projection: rgba(3, 218, 198, 0.5);

            --color-sin: #ff6b6b;
            --color-cos: #4d96ff;
            --color-tan: #f9c74f;
            --color-sec: #90be6d;
            --color-csc: #f8961e;
            --color-cot: #577590;

            --color-sinh: #ff8fab;
            --color-cosh: #bde0fe;
            --color-p-area: rgba(189, 224, 254, 0.2);
        }

        *, *::before, *::after {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
        }

        html {
            font-size: 16px;
            scroll-behavior: smooth;
