                <!-- Trigonometric Triangles -->
                <g id="trig-lines">
                    <!-- Triangle for Cot/Csc -->
                    <line id="line-cot-adj" x1="0" y1="0" x2="0" y2="-1" stroke="var(--color-axis)" stroke-width="0.02" />
                    <line id="line-cot" stroke="var(--color-cot)" stroke-width="0.03" />
                    <line id="line-csc" stroke="var(--color-csc)" stroke-width="0.03" />
                    <!-- Triangle for Tan/Sec -->
                    <line id="line-tan-adj" x1="0" y1="0" x2="1" y2="0" stroke="var(--color-axis)" stroke-width="0.02" />
                    <line id="line-tan" stroke="var(--color-tan)" stroke-width="0.03" />
                    <line id="line-sec" stroke="var(--color-sec)" stroke-width="0.03" />
                    <!-- Triangle for Sin/Cos -->
                    <line id="line-cos" stroke="var(--color-cos)" stroke-width="0.03" />
                    <line id="line-sin" stroke="var(--color-sin)" stroke-width="0.03" />
                    <line id="line-radius" stroke="var(--color-radius)" stroke-width="0.03" />
