                <!-- Points -->
                <circle id="point-P" r="0.04" fill="var(--accent-color)" />
                <circle cx="0" cy="0" r="0.03" fill="var(--bg-color)" stroke="var(--primary-text-color)" stroke-width="0.01" />
            </svg>
        </section>

        <aside class="controls-and-info">
            <div class="control-group" role="group" aria-labelledby="control-group-title-angle">
                <label id="control-group-title-angle" for="angle-slider" class="control-group-title">Angle (θ)</label>
                <input type="range" id="angle-slider" min="0" max="360" step="0.1" value="45" aria-valuetext="Angle: 45.0 degrees">
