                <div class="info-group">
                    <h3>Hyperbolic</h3>
                    <dl>
                        <div class="info-item">
                            <dt><span class="color-swatch" style="background-color: var(--accent-color);" aria-hidden="true"></span>Parameter (p)</dt>
                            <dd id="val-p">0.881</dd>
                        </div>
                        <div class="info-item">
                            <dt><span class="color-swatch" style="background-color: var(--color-sinh);" aria-hidden="true"></span>sinh(p)</dt>
                            <dd id="val-sinh">1.000</dd>
                        </div>
                        <div class="info-item">
                            <dt><span class="color-swatch" style="background-color: var(--color-cosh);" aria-hidden="true"></span>cosh(p)</dt>
                            <dd id="val-cosh">1.414</dd>
                        </div>
                        <div class="info-item">
                            <dt><span class="color-swatch" style="background-color: var(--color-sin);" aria-hidden="true"></span>tanh(p)</dt>
                            <dd id="val-tanh">0.707</dd>
                        </div>
                        <div class="info-item">
                            <dt><span class="color-swatch" style="background-color: var(--color-p-area);" aria-hidden="true"></span>Area (p/2)</dt>
