    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const slider = document.getElementById('angle-slider');

            const elements = {
                angleDisplay: document.getElementById('angle-display'),
                pointP: document.getElementById('point-P'),
                pointH: document.getElementById('point-H'),
                lineRadius: document.getElementById('line-radius'),
                lineSin: document.getElementById('line-sin'),
                lineCos: document.getElementById('line-cos'),
                lineTan: document.getElementById('line-tan'),
                lineSec: document.getElementById('line-sec'),
                lineCsc: document.getElementById('line-csc'),
                lineCot: document.getElementById('line-cot'),
                lineSinh: document.getElementById('line-sinh'),
                lineCosh: document.getElementById('line-cosh'),
                angleArc: document.getElementById('angle-arc'),
                angleLabel: document.getElementById('angle-label'),
                hyperbolicArea: document.getElementById('hyperbolic-area'),
                hyperbolaPath: document.getElementById('hyperbola-path'),
                hyperbolicElements: document.getElementById('hyperbolic-elements'),
                projectionLine: document.getElementById('projection-line'),
                valSin: document.getElementById('val-sin'),
                valCos: document.getElementById('val-cos'),
                valTan: document.getElementById('val-tan'),
                valSec: document.getElementById('val-sec'),
                valCsc: document.getElementById('val-csc'),
                valCot: document.getElementById('val-cot'),
                valP: document.getElementById('val-p'),
                valSinh: document.getElementById('val-sinh'),
                valCosh: document.getElementById('val-cosh'),
                valTanh: document.getElementById('val-tanh'),
                valArea: document.getElementById('val-area'),
            };

            const SVG_BOUNDS = 2.2;
            const EPSILON = 1e-9;

            const state = {
                currentDeg: parseFloat(slider.value),
                targetDeg: parseFloat(slider.value),
                animationFrameId: null,
            };

            function generateHyperbolaPath(maxP, steps = 50) {
                let pathD_top = 'M 1 0 ';
                let pathD_bottom = 'M 1 0 ';
                for (let i = 1; i <= steps; i++) {
                    const p = (i / steps) * maxP;
                    const x = Math.cosh(p);
                    const y = Math.sinh(p);
                    if (x > SVG_BOUNDS) break;
                    pathD_top += `L ${x} ${-y} `;
                    pathD_bottom += `L ${x} ${y} `;
                }
                return pathD_top + pathD_bottom;
            }

            function generateHyperbolicAreaPath(p, steps = 30) {
                if (Math.abs(p) < EPSILON) return 'M 0,0 Z';
                let pathD = 'M 0,0 L 1,0 ';
                for (let i = 1; i <= steps; i++) {
                    const currentP = (i / steps) * p;
                    const x = Math.cosh(currentP);
                    const y = Math.sinh(currentP);
                    pathD += `L ${x} ${-y} `;
                }
                pathD += 'Z';
                return pathD;
            }

            function formatValue(val) {
                if (val === null || val === undefined || isNaN(val)) return 'N/A';
                if (!isFinite(val)) return val > 0 ? '∞' : '−∞';
                return val.toFixed(3);
            }

            function render(deg) {
                const rad = deg * Math.PI / 180;

                // --- CALCULATIONS ---
                let s = Math.sin(rad);
                let c = Math.cos(rad);
                let t, sec, cot, csc;

                if (Math.abs(c) < EPSILON) {
                    c = 0; t = s > 0 ? Infinity : -Infinity; sec = t; csc = 1 / s; cot = 0;
                } else if (Math.abs(s) < EPSILON) {
                    s = 0; t = 0; sec = 1 / c; csc = c > 0 ? Infinity : -Infinity; cot = csc;
                } else {
                    t = s / c; sec = 1 / c; csc = 1 / s; cot = c / s;
                }

                // Point P on unit circle
                const Px = c;
                const Py = -s; // SVG y-axis is inverted

                // --- UPDATE SVG GEOMETRY ---
                elements.pointP.setAttribute('cx', Px);
                elements.pointP.setAttribute('cy', Py);

                // Sin/Cos Triangle
                elements.lineRadius.setAttribute('x2', Px);
                elements.lineRadius.setAttribute('y2', Py);

                elements.lineSin.setAttribute('x1', Px);
                elements.lineSin.setAttribute('y1', Py);
                elements.lineSin.setAttribute('x2', Px);
                elements.lineSin.setAttribute('y2', 0);

                elements.lineCos.setAttribute('x1', Px);
                elements.lineCos.setAttribute('y1', 0);

                // Tan/Sec Triangle
                const tanEndPointY = isFinite(t) ? -t : -Math.sign(t) * SVG_BOUNDS;
                elements.lineTan.setAttribute('y2', tanEndPointY);
                elements.lineSec.setAttribute('y2', tanEndPointY);

                // Cot/Csc Triangle
                const cotEndPointX = isFinite(cot) ? cot : Math.sign(cot) * SVG_BOUNDS;
                elements.lineCot.setAttribute('x2', cotEndPointX);
                elements.lineCsc.setAttribute('x2', cotEndPointX);

                // Angle Arc
                const largeArcFlag = deg % 360 > 180 ? 1 : 0;
                const arcRadius = 0.3;
                const arcPath = `M ${arcRadius} 0 A ${arcRadius} ${arcRadius} 0 ${largeArcFlag} 0 ${arcRadius * c} ${-arcRadius * s}`;
                elements.angleArc.setAttribute('d', arcPath);

                const labelRadius = 0.4;
                elements.angleLabel.setAttribute('x', labelRadius * Math.cos(rad / 2));
                elements.angleLabel.setAttribute('y', -labelRadius * Math.sin(rad / 2));

                // --- HYPERBOLIC CALCULATIONS & VISUALIZATION ---
                let p = NaN, sinh_p = NaN, cosh_p = NaN, tanh_p = NaN;
                const isHyperbolicDefined = sec >= 1 && isFinite(sec);

                if (isHyperbolicDefined) {
                    p = Math.log(sec + t);
                    sinh_p = Math.sinh(p);
                    cosh_p = Math.cosh(p);
                    tanh_p = Math.tanh(p);

                    const Hx = cosh_p;
                    const Hy = -sinh_p;

                    elements.pointH.setAttribute('cx', Hx);
                    elements.pointH.setAttribute('cy', Hy);

                    elements.lineCosh.setAttribute('x1', Hx);
                    elements.lineSinh.setAttribute('x1', Hx);
                    elements.lineSinh.setAttribute('y2', Hy);

                    elements.hyperbolicArea.setAttribute('d', generateHyperbolicAreaPath(p));

                    // Projection line: connects end of tan line to point H, showing tan(θ) = sinh(p)
                    elements.projectionLine.setAttribute('x1', 1);
                    elements.projectionLine.setAttribute('y1', -t);
                    elements.projectionLine.setAttribute('x2', Hx);
                    elements.projectionLine.setAttribute('y2', Hy);

                    elements.hyperbolicElements.style.visibility = 'visible';
                } else {
                    elements.hyperbolicElements.style.visibility = 'hidden';
                }

                // --- UPDATE INFO PANEL ---
                elements.angleDisplay.textContent = `${(deg % 360).toFixed(1)}° / ${(rad % (2*Math.PI)).toFixed(3)} rad`;
                slider.setAttribute('aria-valuetext', `Angle: ${(deg % 360).toFixed(1)} degrees`);

                elements.valSin.textContent = formatValue(s);
                elements.valCos.textContent = formatValue(c);
                elements.valTan.textContent = formatValue(t);
                elements.valSec.textContent = formatValue(sec);
                elements.valCsc.textContent = formatValue(csc);
                elements.valCot.textContent = formatValue(cot);

                elements.valP.textContent = formatValue(p);
                elements.valSinh.textContent = formatValue(sinh_p);
                elements.valCosh.textContent = formatValue(cosh_p);
                elements.valTanh.textContent = formatValue(tanh_p);
                elements.valArea.textContent = formatValue(p / 2);
            }

            function animationLoop() {
                const diff = state.targetDeg - state.currentDeg;
                if (Math.abs(diff) < 0.05) {
                    state.currentDeg = state.targetDeg;
                    state.animationFrameId = null;
                } else {
                    state.currentDeg += diff * 0.15; // Easing
                    state.animationFrameId = requestAnimationFrame(animationLoop);
                }
                render(state.currentDeg);
            }

            function startAnimation() {
                if (!state.animationFrameId) {
                    state.animationFrameId = requestAnimationFrame(animationLoop);
                }
            }

            function handleSliderInput() {
                state.targetDeg = parseFloat(slider.value);
                startAnimation();
            }

            function initialize() {
                const maxP = Math.acosh(SVG_BOUNDS);
                elements.hyperbolaPath.setAttribute('d', generateHyperbolaPath(maxP));

                // Set fixed line coordinates that don't change
                elements.lineRadius.setAttribute('x1', 0);
                elements.lineRadius.setAttribute('y1', 0);
                elements.lineCos.setAttribute('x2', 0);
                elements.lineCos.setAttribute('y2', 0);
                elements.lineTan.setAttribute('x1', 1);
                elements.lineTan.setAttribute('y1', 0);
                elements.lineSec.setAttribute('x1', 0);
                elements.lineSec.setAttribute('y1', 0);
                elements.lineSec.setAttribute('x2', 1);
                elements.lineCot.setAttribute('x1', 0);
                elements.lineCot.setAttribute('y1', -1);
                elements.lineCsc.setAttribute('x1', 0);
                elements.lineCsc.setAttribute('y1', 0);
                elements.lineCsc.setAttribute('y2', -1);
                elements.lineCosh.setAttribute('x2', 0);
                elements.lineCosh.setAttribute('y2', 0);
                elements.lineSinh.setAttribute('y1', 0);

                slider.addEventListener('input', handleSliderInput);
                render(state.currentDeg);
            }

            initialize();
        });
    </script>

</body>
