                highlightElements.forEach(id => document.getElementById(id)?.classList.add('highlight-pulse'));
                await sleep(1200);
                highlightElements.forEach(id => document.getElementById(id)?.classList.remove('highlight-pulse'));

                const createAnimationPromise = (clone, finalStyle) => {
                    return new Promise(resolve => {
                        clone.addEventListener('transitionend', resolve, { once: true });
                        requestAnimationFrame(() => {
                            for (const prop in finalStyle) {
                                clone.style[prop] = finalStyle[prop];
