                // Animate sec -> cosh
                const secClone = elements['line-sec'].cloneNode(true);
                secClone.id = '';
                secClone.classList.add('animating-clone');
                elements.animationLayer.appendChild(secClone);
                const secAnimation = createAnimationPromise(secClone, { transform: `rotate(${-state.currentDeg}deg)` });

                // Animate tan -> sinh
                const tanClone = elements['line-tan'].cloneNode(true);
                tanClone.id = '';
                tanClone.classList.add('animating-clone');
                elements.animationLayer.appendChild(tanClone);
                const tanAnimation = createAnimationPromise(tanClone, { transform: `translateX(${Hx - 1}px)` });

                // Animate circular area -> hyperbolic area
                const areaClone = elements['circular-area'].cloneNode(true);
                areaClone.id = '';
                elements.animationLayer.appendChild(areaClone);
                const finalAreaD = generateAreaPath('hyperbolic', p, ANIMATION_SEGMENTS);
                const areaAnimation = new Promise(resolve => {
                    let start = null;
                    const duration = 800;
                    const startD = areaClone.getAttribute('d');

                    function morph(timestamp) {
                        if (!start) start = timestamp;
                        const progress = Math.min((timestamp - start) / duration, 1);
                        const easedProgress = 0.5 - 0.5 * Math.cos(progress * Math.PI);

                        const interpolatedD = interpolatePath(startD, finalAreaD, easedProgress);
                        areaClone.setAttribute('d', interpolatedD);

                        if (progress < 1) {
                            requestAnimationFrame(morph);
                        } else {
