                elements.animationLayer.innerHTML = '';
                state.isDemoRunning = false;
                slider.disabled = false;
                svg.style.cursor = 'grab';
                render(state.currentDeg);
            }

            function interpolatePath(d1, d2, t) {
                const points1 = d1.match(/[LMCZ] ([\d.-]+) ([\d.-]+)/g).map(s => s.slice(2).split(' ').map(Number));
                const points2 = d2.match(/[LMCZ] ([\d.-]+) ([\d.-]+)/g).map(s => s.slice(2).split(' ').map(Number));
                if (points1.length !== points2.length) return d2;

                let newD = 'M 0 0 ';
                for (let i = 0; i < points1.length; i++) {
                    const x = points1[i][0] * (1 - t) + points2[i][0] * t;
                    const y = points1[i][1] * (1 - t) + points2[i][1] * t;
                    newD += `L ${x} ${y} `;
                }
                return newD + 'Z';
