                const tanAnimation = createAnimationPromise('line-tan', `translate(${Hx - 1} 0)`);

                Promise.all([secAnimation, tanAnimation]).then(() => {
                    setTimeout(() => { // Brief pause to admire the result
                        elements.animationLayer.innerHTML = '';
                        state.isDemoRunning = false;
                        slider.disabled = false;
                        render(state.currentDeg);
                    }, 400);
                });
            }

            function initialize() {
                const maxP = Math.acosh(SVG_BOUNDS);
                elements.hyperbolaPath.setAttribute('d', generateHyperbolaPath(maxP));

                slider.addEventListener('input', () => {
                    state.targetDeg = parseFloat(slider.value);
                    startAnimation();
                });

                demoButton.addEventListener('click', runConnectionDemo);

                TOGGLED_FUNCTIONS.forEach(fn => {
                    if (elements[`toggle-${fn}`]) {
