                });

                const addHighlightListeners = (name1, name2) => {
                    const item1 = elements[`toggle-item-${name1}`];
                    const item2 = elements[`toggle-item-${name2}`];
                    if (!item1 || !item2) return;
                    const line1 = elements[`line-${name1}`];
                    const line2 = elements[`line-${name2}`];

                    const highlightOn = () => {
                        if (item1.closest('.is-disabled')) return;
                        line1.classList.add('highlight-pulse');
                        line2.classList.add('highlight-pulse');
                    };
                    const highlightOff = () => {
                        line1.classList.remove('highlight-pulse');
                        line2.classList.remove('highlight-pulse');
                    };

                    [item1, item2].forEach(item => {
                        item.addEventListener('mouseenter', highlightOn);
                        item.addEventListener('mouseleave', highlightOff);
                        item.querySelectorAll('input, label').forEach(el => {
                            el.addEventListener('focus', highlightOn);
                            el.addEventListener('blur', highlightOff);
                        });
                    });
                };

                addHighlightListeners('sec', 'cosh');
                addHighlightListeners('tan', 'sinh');

                updateVisibility();
                render(state.currentDeg);
            }

            initialize();
        });
