            --slider-progress: 12.5%; /* 45/360 */
        }

        *, *::before, *::after {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
        }

        html {
            font-size: 16px;
            scroll-behavior: smooth;
        }

        body {
            font-family: var(--font-family-sans);
            background-color: var(--bg-color);
            color: var(--primary-text-color);
            display: flex;
            flex-direction: column;
            min-height: 100vh;
            line-height: 1.6;
        }

        .container {
            width: 100%;
            max-width: 1600px;
            margin: 0 auto;
            padding: 1rem;
        }

        header {
            text-align: center;
            padding: 1.5rem 1rem;
            border-bottom: 1px solid var(--surface-color);
        }

        header h1 {
            font-size: clamp(1.75rem, 5vw, 2.5rem);
            color: var(--primary-text-color);
            font-weight: 600;
        }

        header p {
            font-size: clamp(0.9rem, 2.5vw, 1.1rem);
            color: var(--secondary-text-color);
            margin-top: 0.5rem;
            max-width: 800px;
            margin-left: auto;
            margin-right: auto;
        }

        main {
            flex-grow: 1;
            display: grid;
            grid-template-columns: 1fr;
            gap: 2rem;
            padding: 2rem 1rem;
        }

        @media (min-width: 1024px) {
            main {
                grid-template-columns: minmax(0, 1fr) 380px;
                align-items: start;
            }
        }

        .visualization-wrapper {
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: var(--surface-color);
            border-radius: 12px;
            padding: 1rem;
            min-height: 50vh;
            overflow: hidden;
            position: relative;
        }

        #trig-visualization {
            width: 100%;
            height: 100%;
            max-height: 75vh;
            font-family: var(--font-family-mono);
            user-select: none;
            cursor: grab;
        }
        #trig-visualization:active {
            cursor: grabbing;
        }

        .controls-and-info {
            background-color: var(--surface-color);
            border-radius: 12px;
            padding: 1.5rem;
            display: flex;
            flex-direction: column;
            gap: 1.5rem;
        }

        @media (min-width: 1024px) {
            .controls-and-info {
                position: sticky;
                top: 2rem;
                max-height: calc(100vh - 4rem);
                overflow-y: auto;
            }
        }

        .control-group {
            display: flex;
            flex-direction: column;
            gap: 1rem;
        }

        .control-group-title {
            font-weight: 500;
            font-size: 1.1rem;
            color: var(--primary-text-color);
            padding-bottom: 0.5rem;
            border-bottom: 1px solid var(--color-grid);
            margin: 0;
        }

        #angle-display {
            font-family: var(--font-family-mono);
            font-size: 1.1rem;
            color: var(--accent-color);
            font-weight: bold;
            text-align: center;
            background: var(--bg-color);
            padding: 0.5rem;
            border-radius: 6px;
        }

        input[type="range"] {
            -webkit-appearance: none;
            appearance: none;
            width: 100%;
            height: 8px;
            background: linear-gradient(to right, var(--accent-color) var(--slider-progress), var(--bg-color) var(--slider-progress));
            border-radius: 5px;
            outline: none;
            cursor: pointer;
            transition: background 0.1s linear;
        }

        input[type="range"]::-webkit-slider-thumb {
            -webkit-appearance: none;
            appearance: none;
            width: 24px;
            height: 24px;
            background: var(--accent-color);
            border-radius: 50%;
            border: 3px solid var(--surface-color);
            transition: background 0.2s ease-in-out;
        }

        input[type="range"]::-moz-range-thumb {
            width: 24px;
            height: 24px;
            background: var(--accent-color);
            border-radius: 50%;
            border: 3px solid var(--surface-color);
            transition: background 0.2s ease-in-out;
        }

        input[type="range"]:focus-visible {
            outline: 2px solid var(--accent-color);
            outline-offset: 4px;
        }

        input[type="range"]:hover::-webkit-slider-thumb,
        input[type="range"]:focus-visible::-webkit-slider-thumb {
            background: var(--accent-color-dark);
        }
        input[type="range"]:hover::-moz-range-thumb,
        input[type="range"]:focus-visible::-moz-range-thumb {
            background: var(--accent-color-dark);
        }

        .info-panel {
            display: flex;
            flex-direction: column;
            gap: 1rem;
        }

        .info-group {
            display: flex;
            flex-direction: column;
            gap: 0.75rem;
            transition: opacity 0.3s ease;
        }
        .info-group[aria-disabled="true"] {
            opacity: 0.6;
            pointer-events: none;
        }

        .info-group h3 {
            font-size: 1.1rem;
            color: var(--primary-text-color);
            border-bottom: 2px solid var(--accent-color);
            padding-bottom: 0.5rem;
            margin-bottom: 0.5rem;
        }

        .info-item {
            display: flex;
            justify-content: space-between;
            align-items: center;
            font-family: var(--font-family-mono);
            font-size: 0.9rem;
            gap: 1rem;
            transition: opacity 0.2s ease, color 0.2s ease;
        }
        .info-item dt {
            flex-grow: 1;
        }
        .info-item dt label {
            display: flex;
            align-items: center;
            gap: 0.75rem;
            cursor: pointer;
        }
        .info-item.is-static dt {
            cursor: default;
        }
        .info-item dd {
            font-weight: bold;
            color: var(--primary-text-color);
            min-width: 60px;
            text-align: right;
        }
        .info-item .color-swatch {
            width: 12px;
            height: 12px;
            border-radius: 2px;
            flex-shrink: 0;
        }
        .info-item.is-hidden {
            opacity: 0.5;
        }
        .info-item.is-hidden dd {
            color: var(--disabled-text-color);
        }
        .info-item.is-undefined {
            opacity: 0.6;
            color: var(--disabled-text-color);
        }
        .info-item.is-undefined dd {
            color: var(--disabled-text-color);
        }
        .info-item[aria-disabled="true"] {
            color: var(--disabled-text-color);
        }
        .info-item[aria-disabled="true"] dt label {
            cursor: not-allowed;
        }
        .info-item[aria-disabled="true"] dd {
            color: var(--disabled-text-color);
        }

        input[type="checkbox"].visibility-toggle {
            -webkit-appearance: none;
            appearance: none;
            position: relative;
            width: 42px;
            height: 22px;
            background-color: var(--bg-color);
            border-radius: 11px;
            cursor: pointer;
            transition: background-color 0.2s ease;
            border: 1px solid var(--secondary-text-color);
            flex-shrink: 0;
        }
        input[type="checkbox"].visibility-toggle::before {
            content: '';
            position: absolute;
            width: 16px;
            height: 16px;
            border-radius: 50%;
            background-color: var(--secondary-text-color);
            top: 2px;
            left: 2px;
            transition: transform 0.2s ease, background-color 0.2s ease;
        }
        input[type="checkbox"].visibility-toggle:checked {
            background-color: var(--accent-color-dark);
            border-color: var(--accent-color-dark);
        }
        input[type="checkbox"].visibility-toggle:checked::before {
            transform: translateX(20px);
            background-color: var(--surface-color);
        }
        input[type="checkbox"].visibility-toggle:focus-visible {
            outline: 2px solid var(--accent-color);
            outline-offset: 2px;
        }
        input[type="checkbox"].visibility-toggle:disabled {
            background-color: var(--disabled-color);
            border-color: var(--disabled-color);
            cursor: not-allowed;
        }
        input[type="checkbox"].visibility-toggle:disabled::before {
            background-color: var(--secondary-text-color);
        }

        #demo-button {
            background-color: var(--accent-color);
            color: var(--bg-color);
            border: none;
            padding: 0.75rem 1rem;
            border-radius: 6px;
            font-weight: 600;
            cursor: pointer;
            transition: background-color 0.2s ease, opacity 0.2s ease;
            font-size: 1rem;
            width: 100%;
        }
        #demo-button:hover:not(:disabled), #demo-button:focus-visible:not(:disabled) {
            background-color: var(--accent-color-dark);
        }
        #demo-button:disabled {
            background-color: var(--disabled-color);
            color: var(--disabled-text-color);
            cursor: not-allowed;
        }
        .notice-text {
            font-size: 0.85rem;
            color: var(--secondary-text-color);
            background-color: var(--bg-color);
            padding: 0.75rem;
            border-radius: 4px;
            text-align: center;
            border: 1px solid var(--color-grid);
        }

        footer {
            text-align: center;
            padding: 1.5rem 1rem;
            margin-top: auto;
            font-size: 0.9rem;
            color: var(--secondary-text-color);
            border-top: 1px solid var(--surface-color);
        }

        footer a {
            color: var(--accent-color);
            text-decoration: none;
            border-radius: 2px;
            font-weight: 500;
        }

        footer a:hover, footer a:focus-visible {
            text-decoration: underline;
            text-decoration-thickness: 2px;
            text-underline-offset: 3px;
        }
        footer a:focus-visible {
            outline: 2px solid var(--accent-color);
            outline-offset: 2px;
        }

        .visually-hidden {
            clip: rect(0 0 0 0);
            clip-path: inset(50%);
            height: 1px;
            overflow: hidden;
            position: absolute;
            white-space: nowrap;
            width: 1px;
        }

        /* SVG Specific Styles */
        .trig-line {
            transition: opacity 0.3s ease;
        }
        .dynamic-label {
            font-size: 0.12px;
            text-anchor: middle;
            dominant-baseline: middle;
            paint-order: stroke;
            stroke: var(--surface-color);
            stroke-width: 0.05px;
            pointer-events: none;
            transition: transform 0.1s ease-out;
        }
        .leader-line {
            stroke: var(--secondary-text-color);
            stroke-width: 0.01px;
            fill: none;
            opacity: 0.7;
        }
        .angle-label {
            font-size: 0.15px;
            text-anchor: middle;
            dominant-baseline: middle;
            fill: var(--accent-color);
            font-weight: bold;
        }
        .axis-label {
            fill: var(--secondary-text-color);
            font-size: 0.1px;
            text-anchor: middle;
        }
        .highlight-pulse {
            animation: pulse 1.2s infinite ease-in-out;
        }
        @keyframes pulse {
            0%, 100% { stroke-width: 0.03; }
            50% { stroke-width: 0.07; }
        }
        .is-fading-out {
            animation: fadeOut 0.4s ease-out forwards;
        }
        @keyframes fadeOut {
            from { opacity: 1; }
            to { opacity: 0; }
        }
        .animating-clone {
            transition: transform 1s cubic-bezier(0.65, 0, 0.35, 1), stroke 0.5s ease-in-out;
        }
        .demo-annotation {
            font-size: 0.1px;
            fill: var(--primary-text-color);
            text-anchor: middle;
            paint-order: stroke;
            stroke: var(--bg-color);
            stroke-width: 0.04px;
            opacity: 0;
            animation: fadeIn 0.5s ease-out forwards;
        }
        @keyframes fadeIn {
            to { opacity: 1; }
        }
        path {
            transition: d 0.1s linear;
        }
    </style>
</head>
<body>

    <header class="container">
        <h1>Interactive Unit Circle &amp; Hyperbolic Trigonometry</h1>
        <p>Drag the point on the circle or use the slider to change the angle (θ). Use the toggles to show/hide functions. Observe the connection between circular and hyperbolic functions on the unit hyperbola (x² − y² = 1).</p>
    </header>

    <main class="container">
        <section class="visualization-wrapper" aria-labelledby="viz-title">
            <svg id="trig-visualization" viewBox="-2.2 -2.2 4.4 4.4" preserveAspectRatio="xMidYMid meet" role="img" aria-labelledby="viz-title viz-desc" aria-busy="false" lang="en">
                <title id="viz-title">Interactive Trigonometry Visualization</title>
                <desc id="viz-desc">An interactive diagram showing the unit circle and unit hyperbola. A point on the circle can be moved by dragging or using a slider. Lines representing sin, cos, tan, sec, csc, and cot are drawn and their visibility can be toggled. A button demonstrates the geometric relationship between circular functions (sec, tan) and hyperbolic functions (cosh, sinh) through an animation.</desc>

                <defs>
                    <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="0" refY="3.5" orient="auto" fill="var(--color-axis)">
                        <polygon points="0 0, 10 3.5, 0 7" />
                    </marker>
                </defs>

                <g id="grid-group" stroke="var(--color-grid)" stroke-width="0.01">
                    <line x1="-2" y1="-1" x2="2" y2="-1" /><line x1="-2" y1="1" x2="2" y2="1" />
                    <line x1="-1" y1="-2" x2="-1" y2="2" /><line x1="1" y1="-2" x2="1" y2="2" />
                </g>

                <g id="axes-group" stroke="var(--color-axis)" stroke-width="0.02">
                    <line x1="-2.1" y1="0" x2="2.1" y2="0" marker-end="url(#arrowhead)" />
                    <line x1="0" y1="2.1" x2="0" y2="-2.1" marker-end="url(#arrowhead)" />
                    <text class="axis-label" x="2.1" y="-0.1">x</text>
                    <text class="axis-label" x="0.1" y="-2.1">y</text>
                </g>

                <circle cx="0" cy="0" r="1" fill="none" stroke="var(--color-circle)" stroke-width="0.02" />
                <path id="hyperbola-path" fill="none" stroke="var(--color-hyperbola)" stroke-width="0.02" stroke-dasharray="0.05 0.05" />

                <g id="areas-group">
                    <path id="circular-area" fill="var(--color-theta-area)" stroke="none" opacity="0.7" />
                    <path id="hyperbolic-area" fill="var(--color-p-area)" stroke="none" opacity="0.7" />
                </g>

                <g id="hyperbolic-elements">
                    <g id="group-cosh" class="trig-line">
                        <line id="line-cosh" x1="0" y1="0" stroke="var(--color-cosh)" stroke-width="0.03" />
                        <text id="label-cosh" class="dynamic-label" fill="var(--color-cosh)">cosh</text>
                    </g>
                    <g id="group-sinh" class="trig-line">
                        <line id="line-sinh" y1="0" stroke="var(--color-sinh)" stroke-width="0.03" />
                        <text id="label-sinh" class="dynamic-label" fill="var(--color-sinh)">sinh</text>
                    </g>
                    <circle id="point-H" r="0.04" fill="var(--color-hyperbola)" />
                    <line id="projection-line" stroke="var(--color-projection)" stroke-width="0.015" stroke-dasharray="0.04 0.04" />
                </g>

                <path id="angle-arc" fill="none" stroke="var(--color-radius)" stroke-width="0.02" />
                <text id="angle-label" class="angle-label">θ</text>

                <g id="trig-lines">
                    <g id="group-cot" class="trig-line">
                        <line id="line-cot" x1="0" y1="-1" stroke="var(--color-cot)" stroke-width="0.03" />
                        <text id="label-cot" class="dynamic-label" fill="var(--color-cot)">cot</text>
                    </g>
                    <g id="group-csc" class="trig-line">
                        <line id="line-csc" x1="0" y1="0" y2="-1" stroke="var(--color-csc)" stroke-width="0.03" />
                        <text id="label-csc" class="dynamic-label" fill="var(--color-csc)">csc</text>
                    </g>
                    <g id="group-tan" class="trig-line">
                        <line id="line-tan" x1="1" y1="0" stroke="var(--color-tan)" stroke-width="0.03" />
                        <text id="label-tan" class="dynamic-label" fill="var(--color-tan)">tan</text>
                    </g>
                    <g id="group-sec" class="trig-line">
                        <line id="line-sec" x1="0" y1="0" x2="1" stroke="var(--color-sec)" stroke-width="0.03" />
                        <text id="label-sec" class="dynamic-label" fill="var(--color-sec)">sec</text>
                    </g>
                    <g id="group-cos" class="trig-line">
                        <line id="line-cos" x2="0" y2="0" stroke="var(--color-cos)" stroke-width="0.03" />
                        <text id="label-cos" class="dynamic-label" fill="var(--color-cos)">cos</text>
                    </g>
                    <g id="group-sin" class="trig-line">
                        <line id="line-sin" y2="0" stroke="var(--color-sin)" stroke-width="0.03" />
                        <text id="label-sin" class="dynamic-label" fill="var(--color-sin)">sin</text>
                    </g>
                    <line id="line-radius" x1="0" y1="0" stroke="var(--color-radius)" stroke-width="0.03" />
                </g>

                <g id="leader-lines-group"></g>

                <circle id="point-P" r="0.04" fill="var(--accent-color)" style="pointer-events: none;" />
                <circle id="point-P-hitbox" r="0.2" fill="transparent" style="cursor: grab;" tabindex="-1" />
                <circle cx="0" cy="0" r="0.03" fill="var(--bg-color)" stroke="var(--primary-text-color)" stroke-width="0.01" />

                <g id="animation-layer"></g>
            </svg>
        </section>

        <aside class="controls-and-info">
            <div class="control-group" role="group" aria-labelledby="control-group-title-angle">
                <h2 id="control-group-title-angle" class="control-group-title">Angle (θ) Control</h2>
                <label for="angle-slider" class="visually-hidden">Angle (θ)</label>
                <input type="range" id="angle-slider" min="0" max="360" step="0.5" value="45" aria-valuetext="Angle: 45.0 degrees">
                <div id="angle-display" aria-live="polite">45.0° / 0.785 rad</div>
            </div>

            <div class="info-panel" role="group" aria-labelledby="info-panel-title">
                <h2 id="info-panel-title" class="visually-hidden">Function Controls and Values</h2>
                <dl class="info-group">
                    <h3>Trigonometric</h3>
                    <div class="info-item" id="info-item-sin"><dt><label><input type="checkbox" id="toggle-sin" class="visibility-toggle" data-target="sin" checked><span class="color-swatch" style="background-color: var(--color-sin);" role="presentation"></span>sin(θ)</label></dt><dd id="val-sin">0.707</dd></div>
                    <div class="info-item" id="info-item-cos"><dt><label><input type="checkbox" id="toggle-cos" class="visibility-toggle" data-target="cos" checked><span class="color-swatch" style="background-color: var(--color-cos);" role="presentation"></span>cos(θ)</label></dt><dd id="val-cos">0.707</dd></div>
                    <div class="info-item" id="info-item-tan"><dt><label><input type="checkbox" id="toggle-tan" class="visibility-toggle" data-target="tan" checked><span class="color-swatch" style="background-color: var(--color-tan);" role="presentation"></span>tan(θ)</label></dt><dd id="val-tan">1.000</dd></div>
                    <div class="info-item" id="info-item-sec"><dt><label><input type="checkbox" id="toggle-sec" class="visibility-toggle" data-target="sec" checked><span class="color-swatch" style="background-color: var(--color-sec);" role="presentation"></span>sec(θ)</label></dt><dd id="val-sec">1.414</dd></div>
                    <div class="info-item" id="info-item-csc"><dt><label><input type="checkbox" id="toggle-csc" class="visibility-toggle" data-target="csc" checked><span class="color-swatch" style="background-color: var(--color-csc);" role="presentation"></span>csc(θ)</label></dt><dd id="val-csc">1.414</dd></div>
                    <div class="info-item" id="info-item-cot"><dt><label><input type="checkbox" id="toggle-cot" class="visibility-toggle" data-target="cot" checked><span class="color-swatch" style="background-color: var(--color-cot);" role="presentation"></span>cot(θ)</label></dt><dd id="val-cot">1.000</dd></div>
                </dl>
                <dl id="hyperbolic-info-group" class="info-group" aria-describedby="hyperbolic-notice">
                    <h3>Hyperbolic</h3>
                    <p id="hyperbolic-notice" class="notice-text" hidden>Hyperbolic functions are defined when sec(θ) ≥ 1 (approx. -90° to +90°).</p>
                    <div class="info-item" id="info-item-cosh"><dt><label><input type="checkbox" id="toggle-cosh" class="visibility-toggle" data-target="cosh" checked><span class="color-swatch" style="background-color: var(--color-cosh);" role="presentation"></span>cosh(p)</label></dt><dd id="val-cosh">1.414</dd></div>
                    <div class="info-item" id="info-item-sinh"><dt><label><input type="checkbox" id="toggle-sinh" class="visibility-toggle" data-target="sinh" checked><span class="color-swatch" style="background-color: var(--color-sinh);" role="presentation"></span>sinh(p)</label></dt><dd id="val-sinh">1.000</dd></div>
                    <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--color-tan);" role="presentation"></span>tanh(p)</dt><dd id="val-tanh">0.707</dd></div>
                    <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--color-p-area);" role="presentation"></span>Area (p/2)</dt><dd id="val-area">0.441</dd></div>
                    <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--accent-color);" role="presentation"></span>Parameter (p)</dt><dd id="val-p">0.881</dd></div>
                </dl>
            </div>
            <div class="control-group">
                <button id="demo-button" type="button">Demonstrate Connection</button>
            </div>
        </aside>
    </main>

    <footer class="container">
        <p>&copy; 2024 Interactive Trigonometry Explorer. All rights reserved.</p>
    </footer>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const svg = document.getElementById('trig-visualization');
            const slider = document.getElementById('angle-slider');
            const demoButton = document.getElementById('demo-button');

            const elements = {
                angleDisplay: document.getElementById('angle-display'),
                pointP: document.getElementById('point-P'),
                pointPHitbox: document.getElementById('point-P-hitbox'),
                pointH: document.getElementById('point-H'),
                lineRadius: document.getElementById('line-radius'),
                angleArc: document.getElementById('angle-arc'),
                angleLabel: document.getElementById('angle-label'),
                hyperbolaPath: document.getElementById('hyperbola-path'),
                hyperbolicElements: document.getElementById('hyperbolic-elements'),
                hyperbolicInfoGroup: document.getElementById('hyperbolic-info-group'),
                hyperbolicNotice: document.getElementById('hyperbolic-notice'),
                circularArea: document.getElementById('circular-area'),
                hyperbolicArea: document.getElementById('hyperbolic-area'),
                projectionLine: document.getElementById('projection-line'),
                animationLayer: document.getElementById('animation-layer'),
                leaderLinesGroup: document.getElementById('leader-lines-group'),
                valSin: document.getElementById('val-sin'), valCos: document.getElementById('val-cos'),
                valTan: document.getElementById('val-tan'), valSec: document.getElementById('val-sec'),
                valCsc: document.getElementById('val-csc'), valCot: document.getElementById('val-cot'),
                valP: document.getElementById('val-p'), valSinh: document.getElementById('val-sinh'),
                valCosh: document.getElementById('val-cosh'), valTanh: document.getElementById('val-tanh'),
                valArea: document.getElementById('val-area'),
            };

            const ALL_FUNCTIONS = ['sin', 'cos', 'tan', 'sec', 'csc', 'cot', 'sinh', 'cosh'];
            ALL_FUNCTIONS.forEach(fn => {
                elements[`line-${fn}`] = document.getElementById(`line-${fn}`);
                elements[`label-${fn}`] = document.getElementById(`label-${fn}`);
                elements[`group-${fn}`] = document.getElementById(`group-${fn}`);
