                    <line x1="-2" y1="-1" x2="2" y2="-1" /><line x1="-2" y1="1" x2="2" y2="1" />
                    <line x1="-1" y1="-2" x2="-1" y2="2" /><line x1="1" y1="-2" x2="1" y2="2" />
                </g>

                <g id="axes-group" stroke="var(--color-axis)" stroke-width="0.02">
                    <line x1="-2.1" y1="0" x2="2.1" y2="0" marker-end="url(#arrowhead)" />
                    <line x1="0" y1="2.1" x2="0" y2="-2.1" marker-end="url(#arrowhead)" />
                    <text class="axis-label" x="2.1" y="-0.1">x</text>
                    <text class="axis-label" x="0.1" y="-2.1">y</text>
                </g>

                <circle cx="0" cy="0" r="1" fill="none" stroke="var(--color-circle)" stroke-width="0.02" />
                <path id="hyperbola-path" fill="none" stroke="var(--color-hyperbola)" stroke-width="0.02" stroke-dasharray="0.05 0.05" />

                <g id="areas-group">
                    <path id="circular-area" fill="var(--color-theta-area)" stroke="none" opacity="0.7" />
                    <path id="hyperbolic-area" fill="var(--color-p-area)" stroke="none" opacity="0.7" />
                </g>

                <g id="hyperbolic-elements">
                    <g id="group-cosh" class="trig-line">
                        <line id="line-cosh" x1="0" y1="0" stroke="var(--color-cosh)" stroke-width="0.03" />
