            border-color: var(--accent-color-dark);
        }
        input[type="checkbox"].visibility-toggle:checked::before {
            transform: translateX(17px);
            background-color: var(--surface-color);
        }
        input[type="checkbox"].visibility-toggle:focus-visible {
            outline: 2px solid var(--accent-color);
            outline-offset: 2px;
        }

        #demo-button {
            background-color: var(--accent-color);
            color: var(--bg-color);
            border: none;
            padding: 0.75rem 1rem;
            border-radius: 6px;
            font-weight: 600;
            cursor: pointer;
            transition: background-color 0.2s ease, opacity 0.2s ease;
            font-size: 1rem;
            width: 100%;
        }
        #demo-button:hover:not(:disabled), #demo-button:focus-visible:not(:disabled) {
            background-color: var(--accent-color-dark);
        }
        #demo-button:disabled {
            background-color: var(--disabled-color);
            opacity: 0.7;
            cursor: not-allowed;
        }
        .notice-text {
            font-size: 0.85rem;
            color: var(--secondary-text-color);
            background-color: var(--bg-color);
            padding: 0.75rem;
            border-radius: 4px;
            text-align: center;
            border: 1px solid var(--color-grid);
        }

        footer {
            text-align: center;
            padding: 1.5rem 1rem;
            margin-top: auto;
            font-size: 0.9rem;
            color: var(--secondary-text-color);
            border-top: 1px solid var(--surface-color);
        }

        footer a {
            color: var(--accent-color);
            text-decoration: none;
            border-radius: 2px;
            font-weight: 500;
        }

        footer a:hover, footer a:focus-visible {
            text-decoration: underline;
            text-decoration-thickness: 2px;
            text-underline-offset: 3px;
        }
        footer a:focus-visible {
            outline: 2px solid var(--accent-color);
            outline-offset: 2px;
        }

        .visually-hidden {
            clip: rect(0 0 0 0);
            clip-path: inset(50%);
            height: 1px;
            overflow: hidden;
            position: absolute;
            white-space: nowrap;
            width: 1px;
        }

        /* SVG Specific Styles */
        .trig-line {
            transition: opacity 0.3s ease;
        }
        .dynamic-label {
            font-size: 0.12px;
            text-anchor: middle;
            dominant-baseline: middle;
            paint-order: stroke;
            stroke: var(--surface-color);
            stroke-width: 0.05px;
            pointer-events: none;
            transition: transform 0.1s ease-out;
        }
        .angle-label {
            font-size: 0.15px;
            text-anchor: middle;
            dominant-baseline: middle;
            fill: var(--accent-color);
            font-weight: bold;
        }
        .axis-label {
            fill: var(--secondary-text-color);
            font-size: 0.1px;
            text-anchor: middle;
        }
        .highlight-pulse {
            animation: pulse 1.2s infinite ease-in-out;
        }
        @keyframes pulse {
            0%, 100% { stroke-width: 0.03; }
            50% { stroke-width: 0.07; }
        }
        .animating-clone {
            transition: transform 0.8s cubic-bezier(0.65, 0, 0.35, 1);
        }
        path {
            transition: d 0.1s linear;
        }
    </style>
</head>
<body>

    <header class="container">
        <h1>Interactive Unit Circle &amp; Hyperbolic Trigonometry</h1>
        <p>Drag the point on the circle or use the slider to change the angle (θ). Use the toggles to show/hide functions. Observe the connection between circular and hyperbolic functions on the unit hyperbola (x² − y² = 1).</p>
    </header>

    <main class="container">
        <section class="visualization-wrapper" aria-labelledby="viz-title">
            <svg id="trig-visualization" viewBox="-2.2 -2.2 4.4 4.4" preserveAspectRatio="xMidYMid meet" role="img" aria-labelledby="viz-title viz-desc" aria-busy="false">
                <title id="viz-title">Interactive Trigonometry Visualization</title>
                <desc id="viz-desc">An interactive diagram showing the unit circle and unit hyperbola. A point on the circle can be moved by dragging or using a slider. Lines representing sin, cos, tan, sec, csc, and cot are drawn and their visibility can be toggled. A button demonstrates the geometric relationship between circular functions (sec, tan) and hyperbolic functions (cosh, sinh) through an animation.</desc>

                <defs>
                    <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="0" refY="3.5" orient="auto" fill="var(--color-axis)">
                        <polygon points="0 0, 10 3.5, 0 7" />
                    </marker>
                </defs>

                <g id="grid-group" stroke="var(--color-grid)" stroke-width="0.01">
                    <line x1="-2" y1="-1" x2="2" y2="-1" /><line x1="-2" y1="1" x2="2" y2="1" />
                    <line x1="-1" y1="-2" x2="-1" y2="2" /><line x1="1" y1="-2" x2="1" y2="2" />
                </g>

                <g id="axes-group" stroke="var(--color-axis)" stroke-width="0.02">
                    <line x1="-2.1" y1="0" x2="2.1" y2="0" marker-end="url(#arrowhead)" />
                    <line x1="0" y1="2.1" x2="0" y2="-2.1" marker-end="url(#arrowhead)" />
                    <text class="axis-label" x="2.1" y="-0.1">x</text>
                    <text class="axis-label" x="0.1" y="-2.1">y</text>
                </g>

                <circle cx="0" cy="0" r="1" fill="none" stroke="var(--color-circle)" stroke-width="0.02" />
                <path id="hyperbola-path" fill="none" stroke="var(--color-hyperbola)" stroke-width="0.02" stroke-dasharray="0.05 0.05" />

                <g id="areas-group">
                    <path id="circular-area" fill="var(--color-theta-area)" stroke="none" opacity="0.7" />
                    <path id="hyperbolic-area" fill="var(--color-p-area)" stroke="none" opacity="0.7" />
                </g>

                <g id="hyperbolic-elements">
                    <g id="group-cosh" class="trig-line">
                        <line id="line-cosh" x1="0" y1="0" stroke="var(--color-cosh)" stroke-width="0.03" />
                        <text id="label-cosh" class="dynamic-label" fill="var(--color-cosh)">cosh</text>
                    </g>
                    <g id="group-sinh" class="trig-line">
                        <line id="line-sinh" y1="0" stroke="var(--color-sinh)" stroke-width="0.03" />
                        <text id="label-sinh" class="dynamic-label" fill="var(--color-sinh)">sinh</text>
                    </g>
                    <circle id="point-H" r="0.04" fill="var(--color-hyperbola)" />
                    <line id="projection-line" stroke="var(--color-projection)" stroke-width="0.015" stroke-dasharray="0.04 0.04" />
                </g>

                <path id="angle-arc" fill="none" stroke="var(--color-radius)" stroke-width="0.02" />
                <text id="angle-label" class="angle-label">θ</text>

                <g id="trig-lines">
                    <g id="group-cot" class="trig-line">
                        <line id="line-cot" x1="0" y1="-1" stroke="var(--color-cot)" stroke-width="0.03" />
                        <text id="label-cot" class="dynamic-label" fill="var(--color-cot)">cot</text>
                    </g>
                    <g id="group-csc" class="trig-line">
                        <line id="line-csc" x1="0" y1="0" y2="-1" stroke="var(--color-csc)" stroke-width="0.03" />
                        <text id="label-csc" class="dynamic-label" fill="var(--color-csc)">csc</text>
                    </g>
                    <g id="group-tan" class="trig-line">
                        <line id="line-tan" x1="1" y1="0" stroke="var(--color-tan)" stroke-width="0.03" />
                        <text id="label-tan" class="dynamic-label" fill="var(--color-tan)">tan</text>
                    </g>
                    <g id="group-sec" class="trig-line">
                        <line id="line-sec" x1="0" y1="0" x2="1" stroke="var(--color-sec)" stroke-width="0.03" />
                        <text id="label-sec" class="dynamic-label" fill="var(--color-sec)">sec</text>
                    </g>
                    <g id="group-cos" class="trig-line">
                        <line id="line-cos" x2="0" y2="0" stroke="var(--color-cos)" stroke-width="0.03" />
                        <text id="label-cos" class="dynamic-label" fill="var(--color-cos)">cos</text>
                    </g>
                    <g id="group-sin" class="trig-line">
                        <line id="line-sin" y2="0" stroke="var(--color-sin)" stroke-width="0.03" />
                        <text id="label-sin" class="dynamic-label" fill="var(--color-sin)">sin</text>
                    </g>
                    <line id="line-radius" x1="0" y1="0" stroke="var(--color-radius)" stroke-width="0.03" />
                </g>

                <circle id="point-P" r="0.04" fill="var(--accent-color)" style="pointer-events: none;" />
                <circle id="point-P-hitbox" r="0.2" fill="transparent" style="cursor: grab;" />
                <circle cx="0" cy="0" r="0.03" fill="var(--bg-color)" stroke="var(--primary-text-color)" stroke-width="0.01" />

                <g id="animation-layer"></g>
            </svg>
        </section>

        <aside class="controls-and-info">
            <div class="control-group" role="group" aria-labelledby="control-group-title-angle">
                <h2 id="control-group-title-angle" class="control-group-title">Angle (θ) Control</h2>
                <label for="angle-slider" class="visually-hidden">Angle (θ)</label>
                <input type="range" id="angle-slider" min="0" max="360" step="1" value="45" aria-valuetext="Angle: 45.0 degrees">
                <div id="angle-display" aria-live="polite">45.0° / 0.785 rad</div>
            </div>

            <div class="info-panel" role="group" aria-labelledby="info-panel-title">
                <h2 id="info-panel-title" class="visually-hidden">Function Controls and Values</h2>
                <div class="info-group">
                    <h3>Trigonometric</h3>
                    <div id="toggle-item-sin" class="toggle-item"><label for="toggle-sin"><input type="checkbox" id="toggle-sin" class="visibility-toggle" data-target="sin" checked><span class="color-swatch" style="background-color: var(--color-sin);" role="presentation"></span>sin(θ)</label><span id="val-sin" class="info-value">0.707</span></div>
                    <div id="toggle-item-cos" class="toggle-item"><label for="toggle-cos"><input type="checkbox" id="toggle-cos" class="visibility-toggle" data-target="cos" checked><span class="color-swatch" style="background-color: var(--color-cos);" role="presentation"></span>cos(θ)</label><span id="val-cos" class="info-value">0.707</span></div>
                    <div id="toggle-item-tan" class="toggle-item"><label for="toggle-tan"><input type="checkbox" id="toggle-tan" class="visibility-toggle" data-target="tan" checked><span class="color-swatch" style="background-color: var(--color-tan);" role="presentation"></span>tan(θ)</label><span id="val-tan" class="info-value">1.000</span></div>
                    <div id="toggle-item-sec" class="toggle-item"><label for="toggle-sec"><input type="checkbox" id="toggle-sec" class="visibility-toggle" data-target="sec" checked><span class="color-swatch" style="background-color: var(--color-sec);" role="presentation"></span>sec(θ)</label><span id="val-sec" class="info-value">1.414</span></div>
                    <div id="toggle-item-csc" class="toggle-item"><label for="toggle-csc"><input type="checkbox" id="toggle-csc" class="visibility-toggle" data-target="csc" checked><span class="color-swatch" style="background-color: var(--color-csc);" role="presentation"></span>csc(θ)</label><span id="val-csc" class="info-value">1.414</span></div>
                    <div id="toggle-item-cot" class="toggle-item"><label for="toggle-cot"><input type="checkbox" id="toggle-cot" class="visibility-toggle" data-target="cot" checked><span class="color-swatch" style="background-color: var(--color-cot);" role="presentation"></span>cot(θ)</label><span id="val-cot" class="info-value">1.000</span></div>
                </div>
                <div id="hyperbolic-info-group" class="info-group" aria-describedby="hyperbolic-notice">
                    <h3>Hyperbolic</h3>
                    <p id="hyperbolic-notice" class="notice-text" hidden>Hyperbolic functions are defined when sec(θ) ≥ 1 (approx. -90° to +90°).</p>
                    <div id="toggle-item-cosh" class="toggle-item"><label for="toggle-cosh"><input type="checkbox" id="toggle-cosh" class="visibility-toggle" data-target="cosh" checked><span class="color-swatch" style="background-color: var(--color-cosh);" role="presentation"></span>cosh(p)</label><span id="val-cosh" class="info-value">1.414</span></div>
                    <div id="toggle-item-sinh" class="toggle-item"><label for="toggle-sinh"><input type="checkbox" id="toggle-sinh" class="visibility-toggle" data-target="sinh" checked><span class="color-swatch" style="background-color: var(--color-sinh);" role="presentation"></span>sinh(p)</label><span id="val-sinh" class="info-value">1.000</span></div>
                    <div class="toggle-item is-static"><span class="item-label"><span class="color-swatch" style="background-color: var(--color-tan);" role="presentation"></span>tanh(p)</span><span id="val-tanh" class="info-value">0.707</span></div>
                    <div class="toggle-item is-static"><span class="item-label"><span class="color-swatch" style="background-color: var(--color-p-area);" role="presentation"></span>Area (p/2)</span><span id="val-area" class="info-value">0.441</span></div>
                    <div class="toggle-item is-static"><span class="item-label"><span class="color-swatch" style="background-color: var(--accent-color);" role="presentation"></span>Parameter (p)</span><span id="val-p" class="info-value">0.881</span></div>
                </div>
            </div>
            <div class="control-group">
                <button id="demo-button" type="button">Demonstrate Connection</button>
            </div>
        </aside>
    </main>

    <footer class="container">
        <p>&copy; 2024 Interactive Trigonometry Explorer. All rights reserved.</p>
    </footer>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const svg = document.getElementById('trig-visualization');
            const slider = document.getElementById('angle-slider');
            const demoButton = document.getElementById('demo-button');

            const elements = {
                angleDisplay: document.getElementById('angle-display'),
                pointP: document.getElementById('point-P'),
                pointPHitbox: document.getElementById('point-P-hitbox'),
                pointH: document.getElementById('point-H'),
                lineRadius: document.getElementById('line-radius'),
                angleArc: document.getElementById('angle-arc'),
                angleLabel: document.getElementById('angle-label'),
                hyperbolaPath: document.getElementById('hyperbola-path'),
                hyperbolicElements: document.getElementById('hyperbolic-elements'),
                hyperbolicInfoGroup: document.getElementById('hyperbolic-info-group'),
                hyperbolicNotice: document.getElementById('hyperbolic-notice'),
                circularArea: document.getElementById('circular-area'),
                hyperbolicArea: document.getElementById('hyperbolic-area'),
                projectionLine: document.getElementById('projection-line'),
                animationLayer: document.getElementById('animation-layer'),
                valSin: document.getElementById('val-sin'), valCos: document.getElementById('val-cos'),
                valTan: document.getElementById('val-tan'), valSec: document.getElementById('val-sec'),
                valCsc: document.getElementById('val-csc'), valCot: document.getElementById('val-cot'),
                valP: document.getElementById('val-p'), valSinh: document.getElementById('val-sinh'),
                valCosh: document.getElementById('val-cosh'), valTanh: document.getElementById('val-tanh'),
                valArea: document.getElementById('val-area'),
            };

            const TOGGLED_FUNCTIONS = ['sin', 'cos', 'tan', 'sec', 'csc', 'cot', 'sinh', 'cosh'];
            TOGGLED_FUNCTIONS.forEach(fn => {
                elements[`line-${fn}`] = document.getElementById(`line-${fn}`);
                elements[`label-${fn}`] = document.getElementById(`label-${fn}`);
                elements[`group-${fn}`] = document.getElementById(`group-${fn}`);
                elements[`toggle-${fn}`] = document.getElementById(`toggle-${fn}`);
                elements[`toggle-item-${fn}`] = document.getElementById(`toggle-item-${fn}`);
            });

            const SVG_BOUNDS = 2.2;
            const EPSILON = 1e-9;
            const ARC_RADIUS = 0.3;
            const LABEL_RADIUS = 0.4;
            const ANIMATION_SEGMENTS = 50;

            const state = {
                currentDeg: parseFloat(slider.value),
                targetDeg: parseFloat(slider.value),
                animationFrameId: null,
                isDemoRunning: false,
                isDragging: false,
                visibility: {
                    sin: true, cos: true, tan: true, sec: true, csc: true, cot: true, sinh: true, cosh: true
                }
            };

            function formatValue(val) {
                if (val === null || val === undefined || isNaN(val)) return 'N/A';
                if (val === Infinity) return '→ ∞';
                if (val === -Infinity) return '→ −∞';
                return val.toFixed(3);
            }

            function generateHyperbolaPath(maxP, steps = 50) {
                let pathD_top = 'M 1 0 ';
                let pathD_bottom = 'M 1 0 ';
                for (let i = 1; i <= steps; i++) {
                    const p = (i / steps) * maxP;
                    const x = Math.cosh(p);
                    const y = Math.sinh(p);
                    if (x > SVG_BOUNDS) break;
                    pathD_top += `L ${x} ${-y} `;
                    pathD_bottom += `L ${x} ${y} `;
                }
                return pathD_top + pathD_bottom;
            }

            function generateAreaPath(type, param, segments) {
                if (Math.abs(param) < EPSILON) return 'M 0,0 Z';
                let pathD = 'M 0,0 L 1,0 ';
                for (let i = 1; i <= segments; i++) {
                    const t = i / segments;
                    const currentP = t * param;
                    const x = type === 'circular' ? Math.cos(currentP) : Math.cosh(currentP);
                    const y = type === 'circular' ? Math.sin(currentP) : Math.sinh(currentP);
                    pathD += `L ${x.toPrecision(5)} ${(-y).toPrecision(5)} `;
                }
                pathD += 'Z';
                return pathD;
            }

            function updateVisibility() {
                for (const fn in state.visibility) {
                    const isVisible = state.visibility[fn];
                    if (elements[`group-${fn}`]) {
                        elements[`group-${fn}`].style.visibility = isVisible ? 'visible' : 'hidden';
                    }
                    if (elements[`toggle-item-${fn}`]) {
                        elements[`toggle-item-${fn}`].classList.toggle('is-hidden', !isVisible);
                    }
                }
            }

            function getLineIntersectionWithBoundary(x1, y1, x2, y2) {
                const dx = x2 - x1;
                const dy = y2 - y1;
                let t = Infinity;

                if (Math.abs(dx) > EPSILON) {
                    t = Math.min(t, (Math.sign(dx) * SVG_BOUNDS - x1) / dx);
                }
                if (Math.abs(dy) > EPSILON) {
                    t = Math.min(t, (Math.sign(dy) * SVG_BOUNDS - y1) / dy);
                }

                if (t > 0 && t < 1) {
                    return { x: x1 + t * dx, y: y1 + t * dy };
                }
                return { x: x2, y: y2 };
            }

            function updateSvgElements(values) {
                const { rad, s, c, t, sec, csc, cot, Px, Py, p, Hx, Hy, isHyperbolicDefined } = values;

                elements.pointP.setAttribute('cx', Px);
                elements.pointP.setAttribute('cy', Py);
                elements.pointPHitbox.setAttribute('cx', Px);
                elements.pointPHitbox.setAttribute('cy', Py);
                elements.lineRadius.setAttribute('x2', Px);
                elements.lineRadius.setAttribute('y2', Py);

                const largeArcFlag = (state.currentDeg % 360) > 180 ? 1 : 0;
                const arcPath = `M ${ARC_RADIUS} 0 A ${ARC_RADIUS} ${ARC_RADIUS} 0 ${largeArcFlag} 0 ${ARC_RADIUS * c} ${-ARC_RADIUS * s}`;
                elements.angleArc.setAttribute('d', arcPath);
                elements.angleLabel.setAttribute('x', LABEL_RADIUS * Math.cos(rad / 2));
                elements.angleLabel.setAttribute('y', -LABEL_RADIUS * Math.sin(rad / 2));

                elements.circularArea.setAttribute('d', generateAreaPath('circular', rad, ANIMATION_SEGMENTS));

                const labelPositions = {};

                const setLineAndLabel = (name, x1, y1, x2, y2, value) => {
                    const line = elements[`line-${name}`];
                    const group = elements[`group-${name}`];
                    const label = elements[`label-${name}`];

                    let end = { x: x2, y: y2 };
                    let opacity = 1;
                    if (!isFinite(value)) {
                        end = getLineIntersectionWithBoundary(x1, y1, x2, y2);
                        opacity = 0;
                    }

                    line.setAttribute('x1', x1);
                    line.setAttribute('y1', y1);
                    line.setAttribute('x2', end.x);
                    line.setAttribute('y2', end.y);
                    group.style.opacity = opacity;

                    const labelOffset = (name === 'cos' || name === 'cot') ? 0.12 : -0.12;
                    const labelAngleRad = Math.atan2(y2 - y1, x2 - x1);
                    labelPositions[name] = {
                        el: label,
                        x: (x1 + end.x) / 2 + labelOffset * Math.sin(labelAngleRad),
                        y: (y1 + end.y) / 2 - labelOffset * Math.cos(labelAngleRad),
                        angle: (state.currentDeg % 360 + 360) % 360
                    };
                };

                setLineAndLabel('sin', Px, Py, Px, 0, s);
                setLineAndLabel('cos', Px, 0, 0, 0, c);
                setLineAndLabel('tan', 1, 0, 1, -t, t);
                setLineAndLabel('sec', 0, 0, 1, -t, sec);
                setLineAndLabel('csc', 0, 0, cot, -1, csc);
                setLineAndLabel('cot', cot, -1, 0, -1, cot);

                elements.hyperbolicElements.style.visibility = isHyperbolicDefined ? 'visible' : 'hidden';
                if (isHyperbolicDefined) {
                    elements.pointH.setAttribute('cx', Hx);
                    elements.pointH.setAttribute('cy', Hy);
                    setLineAndLabel('cosh', 0, 0, Hx, 0, Hx);
                    setLineAndLabel('sinh', Hx, 0, Hx, Hy, Hy);
                    elements.hyperbolicArea.setAttribute('d', generateAreaPath('hyperbolic', p, ANIMATION_SEGMENTS));
                    elements.projectionLine.setAttribute('x1', 1);
                    elements.projectionLine.setAttribute('y1', -t);
                    elements.projectionLine.setAttribute('x2', Hx);
                    elements.projectionLine.setAttribute('y2', Hy);
                } else {
                    elements.hyperbolicArea.setAttribute('d', 'M 0,0 Z');
                }

                resolveLabelCollisions(labelPositions);
                Object.values(labelPositions).forEach(pos => pos.el.setAttribute('transform', `translate(${pos.x}, ${pos.y})`));
            }

            function resolveLabelCollisions(positions) {
                const angle = positions.cos.angle;
                const nudge = 0.2;
                if (angle < 20 || angle > 340) {
                    positions.cos.y += nudge; positions.sec.y -= nudge;
                }
                if (angle > 70 && angle < 110) {
                    positions.sin.x -= nudge; positions.csc.x += nudge;
                }
                if (angle > 160 && angle < 200) {
                    positions.cos.y -= nudge; positions.sec.y += nudge;
                }
                if (angle > 250 && angle < 290) {
                    positions.sin.x += nudge; positions.csc.x -= nudge;
                }
            }

            function updateInfoPanel(values) {
                const { deg, rad, s, c, t, sec, csc, cot, p, sinh_p, cosh_p, tanh_p, isHyperbolicDefined } = values;

                const normDeg = (deg % 360 + 360) % 360;
                const normRad = (rad % (2 * Math.PI) + 2 * Math.PI) % (2 * Math.PI);
                elements.angleDisplay.textContent = `${normDeg.toFixed(1)}° / ${normRad.toFixed(3)} rad`;
                slider.setAttribute('aria-valuetext', `Angle: ${normDeg.toFixed(1)} degrees`);
                slider.style.setProperty('--slider-progress', `${(normDeg / 360) * 100}%`);

                elements.valSin.textContent = formatValue(s);
                elements.valCos.textContent = formatValue(c);
                elements.valTan.textContent = formatValue(t);
                elements.valSec.textContent = formatValue(sec);
                elements.valCsc.textContent = formatValue(csc);
                elements.valCot.textContent = formatValue(cot);

                elements.valP.textContent = formatValue(p);
                elements.valSinh.textContent = formatValue(sinh_p);
                elements.valCosh.textContent = formatValue(cosh_p);
                elements.valTanh.textContent = formatValue(tanh_p);
                elements.valArea.textContent = formatValue(p / 2);

                elements.hyperbolicInfoGroup.classList.toggle('is-disabled', !isHyperbolicDefined);
                elements.hyperbolicNotice.hidden = isHyperbolicDefined;
                demoButton.disabled = !isHyperbolicDefined || state.isDemoRunning;
                elements['toggle-sinh'].disabled = !isHyperbolicDefined;
                elements['toggle-cosh'].disabled = !isHyperbolicDefined;
            }

            function render(deg) {
