                        <text id="label-sec" class="dynamic-label" fill="var(--color-sec)">sec</text>
                    </g>
                    <g id="group-cos" class="trig-line">
                        <line id="line-cos" x2="0" y2="0" stroke="var(--color-cos)" stroke-width="0.03" />
                        <text id="label-cos" class="dynamic-label" fill="var(--color-cos)">cos</text>
                    </g>
                    <g id="group-sin" class="trig-line">
                        <line id="line-sin" y2="0" stroke="var(--color-sin)" stroke-width="0.03" />
                        <text id="label-sin" class="dynamic-label" fill="var(--color-sin)">sin</text>
                    </g>
                    <line id="line-radius" x1="0" y1="0" stroke="var(--color-radius)" stroke-width="0.03" />
                </g>

                <g id="leader-lines-group"></g>

                <circle id="point-P" r="0.04" fill="var(--accent-color)" style="pointer-events: none;" />
                <circle id="point-P-hitbox" r="0.2" fill="transparent" style="cursor: grab;" tabindex="-1" />
                <circle cx="0" cy="0" r="0.03" fill="var(--bg-color)" stroke="var(--primary-text-color)" stroke-width="0.01" />

                <g id="animation-layer"></g>
            </svg>
        </section>

        <aside class="controls-and-info">
            <div class="control-group" role="group" aria-labelledby="control-group-title-angle">
                <h2 id="control-group-title-angle" class="control-group-title">Angle (θ) Control</h2>
                <label for="angle-slider" class="visually-hidden">Angle (θ)</label>
                <input type="range" id="angle-slider" min="0" max="360" step="0.5" value="45" aria-valuetext="Angle: 45.0 degrees">
                <div id="angle-display" aria-live="polite">45.0° / 0.785 rad</div>
            </div>

            <div class="info-panel" role="group" aria-labelledby="info-panel-title">
                <h2 id="info-panel-title" class="visually-hidden">Function Controls and Values</h2>
                <dl class="info-group">
                    <h3>Trigonometric</h3>
                    <div class="info-item" id="info-item-sin"><dt><label><input type="checkbox" id="toggle-sin" class="visibility-toggle" data-target="sin" checked><span class="color-swatch" style="background-color: var(--color-sin);" role="presentation"></span>sin(θ)</label></dt><dd id="val-sin">0.707</dd></div>
                    <div class="info-item" id="info-item-cos"><dt><label><input type="checkbox" id="toggle-cos" class="visibility-toggle" data-target="cos" checked><span class="color-swatch" style="background-color: var(--color-cos);" role="presentation"></span>cos(θ)</label></dt><dd id="val-cos">0.707</dd></div>
                    <div class="info-item" id="info-item-tan"><dt><label><input type="checkbox" id="toggle-tan" class="visibility-toggle" data-target="tan" checked><span class="color-swatch" style="background-color: var(--color-tan);" role="presentation"></span>tan(θ)</label></dt><dd id="val-tan">1.000</dd></div>
                    <div class="info-item" id="info-item-sec"><dt><label><input type="checkbox" id="toggle-sec" class="visibility-toggle" data-target="sec" checked><span class="color-swatch" style="background-color: var(--color-sec);" role="presentation"></span>sec(θ)</label></dt><dd id="val-sec">1.414</dd></div>
                    <div class="info-item" id="info-item-csc"><dt><label><input type="checkbox" id="toggle-csc" class="visibility-toggle" data-target="csc" checked><span class="color-swatch" style="background-color: var(--color-csc);" role="presentation"></span>csc(θ)</label></dt><dd id="val-csc">1.414</dd></div>
                    <div class="info-item" id="info-item-cot"><dt><label><input type="checkbox" id="toggle-cot" class="visibility-toggle" data-target="cot" checked><span class="color-swatch" style="background-color: var(--color-cot);" role="presentation"></span>cot(θ)</label></dt><dd id="val-cot">1.000</dd></div>
                </dl>
                <dl id="hyperbolic-info-group" class="info-group" aria-describedby="hyperbolic-notice">
                    <h3>Hyperbolic</h3>
                    <p id="hyperbolic-notice" class="notice-text" hidden>Hyperbolic functions are defined when sec(θ) ≥ 1 (approx. -90° to +90°).</p>
                    <div class="info-item" id="info-item-cosh"><dt><label><input type="checkbox" id="toggle-cosh" class="visibility-toggle" data-target="cosh" checked><span class="color-swatch" style="background-color: var(--color-cosh);" role="presentation"></span>cosh(p)</label></dt><dd id="val-cosh">1.414</dd></div>
                    <div class="info-item" id="info-item-sinh"><dt><label><input type="checkbox" id="toggle-sinh" class="visibility-toggle" data-target="sinh" checked><span class="color-swatch" style="background-color: var(--color-sinh);" role="presentation"></span>sinh(p)</label></dt><dd id="val-sinh">1.000</dd></div>
                    <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--color-tan);" role="presentation"></span>tanh(p)</dt><dd id="val-tanh">0.707</dd></div>
                    <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--color-p-area);" role="presentation"></span>Area (p/2)</dt><dd id="val-area">0.441</dd></div>
                    <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--accent-color);" role="presentation"></span>Parameter (p)</dt><dd id="val-p">0.881</dd></div>
                </dl>
            </div>
            <div class="control-group">
                <button id="demo-button" type="button">Demonstrate Connection</button>
            </div>
        </aside>
    </main>

    <footer class="container">
        <p>&copy; 2024 Interactive Trigonometry Explorer. All rights reserved.</p>
    </footer>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const svg = document.getElementById('trig-visualization');
            const slider = document.getElementById('angle-slider');
            const demoButton = document.getElementById('demo-button');

            const elements = {
                angleDisplay: document.getElementById('angle-display'),
                pointP: document.getElementById('point-P'),
                pointPHitbox: document.getElementById('point-P-hitbox'),
                pointH: document.getElementById('point-H'),
                lineRadius: document.getElementById('line-radius'),
                angleArc: document.getElementById('angle-arc'),
                angleLabel: document.getElementById('angle-label'),
                hyperbolaPath: document.getElementById('hyperbola-path'),
                hyperbolicElements: document.getElementById('hyperbolic-elements'),
                hyperbolicInfoGroup: document.getElementById('hyperbolic-info-group'),
                hyperbolicNotice: document.getElementById('hyperbolic-notice'),
                circularArea: document.getElementById('circular-area'),
                hyperbolicArea: document.getElementById('hyperbolic-area'),
                projectionLine: document.getElementById('projection-line'),
                animationLayer: document.getElementById('animation-layer'),
                leaderLinesGroup: document.getElementById('leader-lines-group'),
                valSin: document.getElementById('val-sin'), valCos: document.getElementById('val-cos'),
                valTan: document.getElementById('val-tan'), valSec: document.getElementById('val-sec'),
                valCsc: document.getElementById('val-csc'), valCot: document.getElementById('val-cot'),
                valP: document.getElementById('val-p'), valSinh: document.getElementById('val-sinh'),
                valCosh: document.getElementById('val-cosh'), valTanh: document.getElementById('val-tanh'),
                valArea: document.getElementById('val-area'),
            };

            const ALL_FUNCTIONS = ['sin', 'cos', 'tan', 'sec', 'csc', 'cot', 'sinh', 'cosh'];
            ALL_FUNCTIONS.forEach(fn => {
                elements[`line-${fn}`] = document.getElementById(`line-${fn}`);
                elements[`label-${fn}`] = document.getElementById(`label-${fn}`);
                elements[`group-${fn}`] = document.getElementById(`group-${fn}`);
                elements[`toggle-${fn}`] = document.getElementById(`toggle-${fn}`);
                elements[`info-item-${fn}`] = document.getElementById(`info-item-${fn}`);
            });

            const SVG_BOUNDS = 2.2;
            const EPSILON = 1e-9;
            const ARC_RADIUS = 0.3;
            const LABEL_RADIUS = 0.4;
            const ANIMATION_SEGMENTS = 50;

            const state = {
                currentDeg: parseFloat(slider.value),
                targetDeg: parseFloat(slider.value),
                animationFrameId: null,
                isDemoRunning: false,
                isDragging: false,
                visibility: {
                    sin: true, cos: true, tan: true, sec: true, csc: true, cot: true, sinh: true, cosh: true
                }
            };

            function formatValue(val) {
                if (val === null || val === undefined || isNaN(val)) return 'N/A';
                if (val === Infinity) return '→ ∞';
                if (val === -Infinity) return '→ −∞';
                return val.toFixed(3);
            }

            function generateHyperbolaPath(maxP, steps = 50) {
                let pathD_top = 'M 1 0 ';
                let pathD_bottom = 'M 1 0 ';
                for (let i = 1; i <= steps; i++) {
                    const p = (i / steps) * maxP;
                    const x = Math.cosh(p);
                    const y = Math.sinh(p);
                    if (x > SVG_BOUNDS) break;
                    pathD_top += `L ${x} ${-y} `;
                    pathD_bottom += `L ${x} ${y} `;
                }
                return pathD_top + pathD_bottom;
            }

            function generateAreaPath(type, param, segments) {
                if (Math.abs(param) < EPSILON) return 'M 0,0 Z';
                let pathD = 'M 0,0 L 1,0 ';
                for (let i = 1; i <= segments; i++) {
                    const t = i / segments;
                    const currentP = t * param;
                    const x = type === 'circular' ? Math.cos(currentP) : Math.sin(currentP);
                    const y = type === 'circular' ? Math.sin(currentP) : Math.sinh(currentP);
                    pathD += `L ${x.toPrecision(5)} ${(-y).toPrecision(5)} `;
                }
                pathD += 'Z';
                return pathD;
            }

            function updateVisibility() {
                for (const fn in state.visibility) {
                    const isVisible = state.visibility[fn];
                    if (elements[`group-${fn}`]) {
                        elements[`group-${fn}`].style.visibility = isVisible ? 'visible' : 'hidden';
                    }
                    if (elements[`info-item-${fn}`]) {
                        elements[`info-item-${fn}`].classList.toggle('is-hidden', !isVisible);
                    }
                }
            }

            function updateSvgElements(values) {
                const { rad, s, c, t, sec, csc, cot, Px, Py, p, Hx, Hy, isHyperbolicDefined } = values;

                elements.pointP.setAttribute('cx', Px);
                elements.pointP.setAttribute('cy', Py);
                elements.pointPHitbox.setAttribute('cx', Px);
                elements.pointPHitbox.setAttribute('cy', Py);
                elements.lineRadius.setAttribute('x2', Px);
                elements.lineRadius.setAttribute('y2', Py);

                const largeArcFlag = (state.currentDeg % 360) > 180 ? 1 : 0;
                const arcPath = `M ${ARC_RADIUS} 0 A ${ARC_RADIUS} ${ARC_RADIUS} 0 ${largeArcFlag} 0 ${ARC_RADIUS * c} ${-ARC_RADIUS * s}`;
                elements.angleArc.setAttribute('d', arcPath);
                elements.angleLabel.setAttribute('x', LABEL_RADIUS * Math.cos(rad / 2));
                elements.angleLabel.setAttribute('y', -LABEL_RADIUS * Math.sin(rad / 2));

                elements.circularArea.setAttribute('d', generateAreaPath('circular', rad, ANIMATION_SEGMENTS));

                const labelData = {};

                const setLineAndLabel = (name, x1, y1, x2, y2) => {
                    elements[`line-${name}`].setAttribute('x1', x1);
                    elements[`line-${name}`].setAttribute('y1', y1);
                    elements[`line-${name}`].setAttribute('x2', x2);
                    elements[`line-${name}`].setAttribute('y2', y2);

                    const labelOffset = (name === 'cos' || name === 'cot' || name === 'cosh') ? 0.12 : -0.12;
                    const labelAngleRad = Math.atan2(y2 - y1, x2 - x1);
                    labelData[name] = {
                        el: elements[`label-${name}`],
                        anchorX: (x1 + x2) / 2,
                        anchorY: (y1 + y2) / 2,
                        offsetX: labelOffset * Math.sin(labelAngleRad),
                        offsetY: -labelOffset * Math.cos(labelAngleRad),
                        x: 0, y: 0,
                        bbox: null
                    };
                };

                setLineAndLabel('sin', Px, Py, Px, 0);
                setLineAndLabel('cos', Px, 0, 0, 0);
                setLineAndLabel('tan', 1, 0, 1, -t);
                setLineAndLabel('sec', 0, 0, 1, -t);
                setLineAndLabel('csc', 0, 0, cot, -1);
                setLineAndLabel('cot', cot, -1, 0, -1);

                elements.hyperbolicElements.style.visibility = isHyperbolicDefined ? 'visible' : 'hidden';
                if (isHyperbolicDefined) {
                    elements.pointH.setAttribute('cx', Hx);
                    elements.pointH.setAttribute('cy', Hy);
                    setLineAndLabel('cosh', 0, 0, Hx, 0);
                    setLineAndLabel('sinh', Hx, 0, Hx, Hy);
                    elements.hyperbolicArea.setAttribute('d', generateAreaPath('hyperbolic', p, ANIMATION_SEGMENTS));
                    elements.projectionLine.setAttribute('x1', 1);
                    elements.projectionLine.setAttribute('y1', -t);
                    elements.projectionLine.setAttribute('x2', Hx);
                    elements.projectionLine.setAttribute('y2', Hy);
                } else {
                    elements.hyperbolicArea.setAttribute('d', 'M 0,0 Z');
                }

                resolveLabelCollisions(labelData);
            }

            function resolveLabelCollisions(labelData) {
                elements.leaderLinesGroup.innerHTML = '';
                const activeLabels = Object.values(labelData).filter(d => state.visibility[d.el.id.replace('label-', '')]);

                activeLabels.forEach(d => {
                    d.x = d.anchorX + d.offsetX;
                    d.y = d.anchorY + d.offsetY;
                    d.el.setAttribute('transform', `translate(${d.x}, ${d.y})`);
                    d.bbox = d.el.getBBox();
                });

                const checkOverlap = (d1, d2) => {
                    const pad = 0.05;
                    return d1.x - d1.bbox.width / 2 - pad < d2.x + d2.bbox.width / 2 + pad &&
                           d1.x + d1.bbox.width / 2 + pad > d2.x - d2.bbox.width / 2 - pad &&
                           d1.y - d1.bbox.height / 2 - pad < d2.y + d2.bbox.height / 2 + pad &&
                           d1.y + d1.bbox.height / 2 + pad > d2.y - d2.bbox.height / 2 - pad;
                };

                for (let i = 0; i < 5; i++) { // Iterative nudging
                    let collisions = 0;
                    for (let j = 0; j < activeLabels.length; j++) {
                        for (let k = j + 1; k < activeLabels.length; k++) {
                            const d1 = activeLabels[j];
                            const d2 = activeLabels[k];
                            if (checkOverlap(d1, d2)) {
                                collisions++;
                                const dx = d2.x - d1.x;
                                const dy = d2.y - d1.y;
                                const dist = Math.sqrt(dx * dx + dy * dy) + EPSILON;
                                const move = 0.05 / dist;
                                d1.x -= dx * move; d1.y -= dy * move;
                                d2.x += dx * move; d2.y += dy * move;
                            }
                        }
                    }
                    if (collisions === 0) break;
                }

                activeLabels.forEach(d => {
                    d.el.setAttribute('transform', `translate(${d.x}, ${d.y})`);
                    const distMoved = Math.sqrt(Math.pow(d.x - (d.anchorX + d.offsetX), 2) + Math.pow(d.y - (d.anchorY + d.offsetY), 2));
                    if (distMoved > 0.02) {
                        const leader = document.createElementNS('http://www.w3.org/2000/svg', 'path');
                        leader.setAttribute('class', 'leader-line');
                        leader.setAttribute('d', `M ${d.anchorX} ${d.anchorY} L ${d.x} ${d.y}`);
                        elements.leaderLinesGroup.appendChild(leader);
                    }
                });
            }

            function updateInfoPanel(values) {
                const { deg, rad, s, c, t, sec, csc, cot, p, sinh_p, cosh_p, tanh_p, isHyperbolicDefined } = values;

                const normDeg = (deg % 360 + 360) % 360;
                const normRad = (rad % (2 * Math.PI) + 2 * Math.PI) % (2 * Math.PI);
                elements.angleDisplay.textContent = `${normDeg.toFixed(1)}° / ${normRad.toFixed(3)} rad`;
                slider.setAttribute('aria-valuetext', `Angle: ${normDeg.toFixed(1)} degrees`);
                slider.style.setProperty('--slider-progress', `${(normDeg / 360) * 100}%`);

                const valueMap = { sin: s, cos: c, tan: t, sec: sec, csc: csc, cot: cot, p: p, sinh: sinh_p, cosh: cosh_p, tanh: tanh_p };

                for (const fnName in valueMap) {
                    const value = valueMap[fnName];
                    const valEl = elements[`val-${fnName}`];
                    if (valEl) valEl.textContent = formatValue(value);

                    const itemEl = elements[`info-item-${fnName}`];
                    if (itemEl) {
                        const isUndef = !isFinite(value);
                        itemEl.classList.toggle('is-undefined', isUndef);

                        const toggleEl = elements[`toggle-${fnName}`];
                        if (toggleEl) {
                            toggleEl.disabled = isUndef;
                            itemEl.setAttribute('aria-disabled', isUndef);
                        }
                    }
                }
                elements.valArea.textContent = formatValue(p / 2);

                elements.hyperbolicInfoGroup.setAttribute('aria-disabled', !isHyperbolicDefined);
                elements.hyperbolicNotice.hidden = isHyperbolicDefined;
                demoButton.disabled = !isHyperbolicDefined || state.isDemoRunning;
            }

            function render(deg) {
                const rad = deg * Math.PI / 180;
                let s = Math.sin(rad);
                let c = Math.cos(rad);
                let t, sec, csc, cot;

                if (Math.abs(c) < EPSILON) {
                    c = 0; t = s > 0 ? Infinity : -Infinity; sec = t; csc = 1 / s; cot = 0;
                } else if (Math.abs(s) < EPSILON) {
                    s = 0; t = 0; sec = 1 / c; csc = c > 0 ? Infinity : -Infinity; cot = csc;
                } else {
                    t = s / c; sec = 1 / c; csc = 1 / s; cot = c / s;
                }

                const Px = c;
                const Py = -s;

                let p = NaN, sinh_p = NaN, cosh_p = NaN, tanh_p = NaN, Hx = NaN, Hy = NaN;
                const isHyperbolicDefined = sec >= 1 - EPSILON && isFinite(sec);

                if (isHyperbolicDefined) {
                    p = Math.acosh(sec);
                    sinh_p = Math.sinh(p);
                    cosh_p = Math.cosh(p);
                    tanh_p = Math.tanh(p);
                    Hx = cosh_p;
                    Hy = -sinh_p;
                }

                const values = { deg, rad, s, c, t, sec, csc, cot, Px, Py, p, sinh_p, cosh_p, tanh_p, Hx, Hy, isHyperbolicDefined };

                updateSvgElements(values);
                updateInfoPanel(values);
            }

            function animationLoop() {
                const diff = state.targetDeg - state.currentDeg;
                if (Math.abs(diff) < 0.05) {
                    state.currentDeg = state.targetDeg;
                    cancelAnimationFrame(state.animationFrameId);
                    state.animationFrameId = null;
                } else {
                    state.currentDeg += diff * 0.15; // Easing
                    state.animationFrameId = requestAnimationFrame(animationLoop);
                }
                render(state.currentDeg);
            }

            function startAnimation() {
                if (!state.animationFrameId) {
                    state.animationFrameId = requestAnimationFrame(animationLoop);
                }
            }

            const sleep = ms => new Promise(resolve => setTimeout(resolve, ms));

            function createAnnotation(text, x, y) {
                const textEl = document.createElementNS('http://www.w3.org/2000/svg', 'text');
                textEl.setAttribute('class', 'demo-annotation');
                textEl.setAttribute('x', x);
                textEl.setAttribute('y', y);
                textEl.textContent = text;
                elements.animationLayer.appendChild(textEl);
                return textEl;
            }

            /**
             * Linearly interpolates the numerical values within two SVG path 'd' strings.
             * Assumes both paths have the same structure and number of points.
             * @param {string} d1 - The starting path data.
             * @param {string} d2 - The ending path data.
             * @param {number} t - The interpolation factor (0.0 to 1.0).
             * @returns {string} The interpolated path data.
             */
            function interpolatePath(d1, d2, t) {
                const numRegex = /-?[\d.e-]+/g;
                const nums1 = d1.match(numRegex)?.map(Number) || [];
                const nums2 = d2.match(numRegex)?.map(Number) || [];

                if (nums1.length !== nums2.length || nums1.length === 0) return d2;

                let i = 0;
                return d1.replace(numRegex, () => {
                    const val = nums1[i] * (1 - t) + nums2[i] * t;
                    i++;
                    return val.toFixed(5);
                });
            }

            async function runConnectionDemo() {
                if (state.isDemoRunning) return;
                state.isDemoRunning = true;
                slider.disabled = true;
                demoButton.disabled = true;
                svg.style.cursor = 'wait';
                svg.setAttribute('aria-busy', 'true');
                elements.animationLayer.innerHTML = '';

                const rad = state.currentDeg * Math.PI / 180;
                const secVal = 1 / Math.cos(rad);
                const p = Math.acosh(secVal);
                const Hx = Math.cosh(p);

                // Stage 1: Highlight sec and tan
                const annotation1 = createAnnotation('sec(θ) and tan(θ) define the point', 0, 1.8);
                elements['group-sec'].classList.add('highlight-pulse');
                elements['group-tan'].classList.add('highlight-pulse');
                await sleep(2500);
                elements['group-sec'].classList.remove('highlight-pulse');
                elements['group-tan'].classList.remove('highlight-pulse');
                annotation1.remove();

                // Stage 2 & 3: Animate sec -> cosh and tan -> sinh
                const annotation2 = createAnnotation('sec(θ) → cosh(p)', -1.2, 1.8);
                const annotation3 = createAnnotation('tan(θ) → sinh(p)', 1.2, 1.8);

                const secClone = elements['line-sec'].cloneNode(true);
                secClone.id = ''; secClone.classList.add('animating-clone');
                elements.animationLayer.appendChild(secClone);

                const tanClone = elements['line-tan'].cloneNode(true);
                tanClone.id = ''; tanClone.classList.add('animating-clone');
                elements.animationLayer.appendChild(tanClone);

                const secAnimation = new Promise(resolve => {
                    secClone.addEventListener('transitionend', resolve, { once: true });
                    requestAnimationFrame(() => {
                        secClone.setAttribute('transform', `rotate(${-state.currentDeg} 0 0)`);
                        secClone.style.stroke = 'var(--color-cosh)';
                    });
                });

                const tanAnimation = new Promise(resolve => {
                    tanClone.addEventListener('transitionend', resolve, { once: true });
                    requestAnimationFrame(() => {
                        tanClone.setAttribute('transform', `translate(${Hx - 1} 0)`);
                        tanClone.style.stroke = 'var(--color-sinh)';
                    });
                });

                await Promise.all([secAnimation, tanAnimation]);
                await sleep(2500);
                annotation2.remove();
                annotation3.remove();

                // Stage 4: Morph area
                const annotation4 = createAnnotation('Area: θ/2 = p/2', 0, 1.8);
                const areaClone = elements['circular-area'].cloneNode(true);
                areaClone.id = '';
                elements.animationLayer.appendChild(areaClone);
                const finalAreaD = generateAreaPath('hyperbolic', p, ANIMATION_SEGMENTS);
                const areaAnimation = new Promise(resolve => {
                    let start = null;
                    const duration = 1000;
                    const startD = areaClone.getAttribute('d');
                    const ease = t => t < 0.5 ? 4 * t * t * t : 1 - Math.pow(-2 * t + 2, 3) / 2;

                    function morph(timestamp) {
                        if (!start) start = timestamp;
                        const progress = Math.min((timestamp - start) / duration, 1);
                        areaClone.setAttribute('d', interpolatePath(startD, finalAreaD, ease(progress)));
                        if (progress < 1) requestAnimationFrame(morph);
                        else resolve();
                    }
                    requestAnimationFrame(morph);
                });

                await areaAnimation;
                await sleep(2500);

                // Cleanup
                elements.animationLayer.innerHTML = '';
                state.isDemoRunning = false;
                slider.disabled = false;
                svg.style.cursor = 'grab';
                svg.setAttribute('aria-busy', 'false');
                render(state.currentDeg);
            }

            function getSVGPoint(event) {
                const pt = svg.createSVGPoint();
                const touch = event.touches ? event.touches[0] : event;
                pt.x = touch.clientX;
                pt.y = touch.clientY;
                return pt.matrixTransform(svg.getScreenCTM().inverse());
            }

            function handleDragStart(e) {
                if (state.isDemoRunning) return;
                e.preventDefault();
                state.isDragging = true;
                svg.style.cursor = 'grabbing';
                document.body.style.cursor = 'grabbing';
                handleDragMove(e);
            }

            function handleDragMove(e) {
                if (!state.isDragging) return;
                e.preventDefault();
                const { x, y } = getSVGPoint(e);
                let angleRad = Math.atan2(-y, x);
