                        <text id="label-cos" class="dynamic-label" fill="var(--color-cos)">cos</text>
                    </g>
                    <g id="group-sin" class="trig-line" data-focus-group="sin">
                        <line id="line-sin" y2="0" stroke="var(--color-sin)" stroke-width="0.03" />
                        <text id="label-sin" class="dynamic-label" fill="var(--color-sin)">sin</text>
                    </g>
                    <line id="line-radius" x1="0" y1="0" stroke="var(--color-radius)" stroke-width="0.03" />
                </g>

                <g id="animation-layer"></g>

                <circle id="point-P" r="0.04" fill="var(--accent-color)" style="pointer-events: none;" />
                <circle id="point-P-hitbox" r="0.2" fill="transparent" style="cursor: grab;" tabindex="-1" aria-hidden="true" />
                <circle cx="0" cy="0" r="0.03" fill="var(--bg-color)" stroke="var(--primary-text-color)" stroke-width="0.01" />
            </svg>
        </section>

        <aside class="controls-and-info">
            <div class="control-group" role="group" aria-labelledby="control-group-title-angle">
                <h2 id="control-group-title-angle" class="control-group-title">Angle (θ) Control</h2>
                <label for="angle-slider" class="visually-hidden">Angle (θ)</label>
                <input type="range" id="angle-slider" min="0" max="360" step="0.5" value="45" aria-valuetext="Angle: 45.0 degrees">
                <div id="angle-display" aria-live="polite">45.0° / 0.785 rad</div>
            </div>

            <div class="control-group">
                <button id="demo-toggle-button" type="button" aria-pressed="false">Start Interactive Demo</button>
                <div id="demo-controls" class="demo-controls" hidden>
                    <div id="demo-explanation" class="demo-explanation" aria-live="polite" role="status"></div>
                    <label for="demo-progress" class="visually-hidden">Demo Step</label>
                    <input type="range" id="demo-progress" min="0" max="4" step="1" value="0">
                    <div class="demo-nav">
                        <button id="demo-prev" type="button">Previous</button>
                        <button id="demo-next" type="button">Next</button>
                    </div>
                </div>
            </div>

            <div class="info-panel" role="group" aria-labelledby="info-panel-title">
                <h2 id="info-panel-title" class="visually-hidden">Function Controls and Values</h2>
                <div class="info-group" id="trig-info-group">
                    <h3 id="trig-info-heading">Trigonometric</h3>
                    <dl aria-labelledby="trig-info-heading">
                        <div class="info-item" id="info-item-sin" data-focus-group="sin"><dt><label><input type="checkbox" id="toggle-sin" class="visibility-toggle" data-target="sin" checked><span class="color-swatch" style="background-color: var(--color-sin);" aria-hidden="true"></span>sin(θ)</label></dt><dd id="val-sin">0.707</dd></div>
                        <div class="info-item" id="info-item-cos" data-focus-group="cos"><dt><label><input type="checkbox" id="toggle-cos" class="visibility-toggle" data-target="cos" checked><span class="color-swatch" style="background-color: var(--color-cos);" aria-hidden="true"></span>cos(θ)</label></dt><dd id="val-cos">0.707</dd></div>
                        <div class="info-item" id="info-item-tan" data-focus-group="tan"><dt><label><input type="checkbox" id="toggle-tan" class="visibility-toggle" data-target="tan" checked><span class="color-swatch" style="background-color: var(--color-tan);" aria-hidden="true"></span>tan(θ)</label></dt><dd id="val-tan">1.000</dd></div>
                        <div class="info-item" id="info-item-sec" data-focus-group="sec"><dt><label><input type="checkbox" id="toggle-sec" class="visibility-toggle" data-target="sec" checked><span class="color-swatch" style="background-color: var(--color-sec);" aria-hidden="true"></span>sec(θ)</label></dt><dd id="val-sec">1.414</dd></div>
                        <div class="info-item" id="info-item-csc" data-focus-group="csc"><dt><label><input type="checkbox" id="toggle-csc" class="visibility-toggle" data-target="csc" checked><span class="color-swatch" style="background-color: var(--color-csc);" aria-hidden="true"></span>csc(θ)</label></dt><dd id="val-csc">1.414</dd></div>
                        <div class="info-item" id="info-item-cot" data-focus-group="cot"><dt><label><input type="checkbox" id="toggle-cot" class="visibility-toggle" data-target="cot" checked><span class="color-swatch" style="background-color: var(--color-cot);" aria-hidden="true"></span>cot(θ)</label></dt><dd id="val-cot">1.000</dd></div>
                    </dl>
                </div>
                <div class="info-group" id="hyperbolic-info-group" aria-describedby="hyperbolic-notice">
                    <h3 id="hyperbolic-info-heading">Hyperbolic</h3>
                    <p id="hyperbolic-notice" class="notice-text" hidden>Hyperbolic functions are defined when sec(θ) ≥ 1 (approx. -90° to +90°).</p>
                    <dl aria-labelledby="hyperbolic-info-heading">
                        <div class="info-item" id="info-item-cosh" data-focus-group="cosh"><dt><label><input type="checkbox" id="toggle-cosh" class="visibility-toggle" data-target="cosh" checked><span class="color-swatch" style="background-color: var(--color-cosh);" aria-hidden="true"></span>cosh(p)</label></dt><dd id="val-cosh">1.414</dd></div>
                        <div class="info-item" id="info-item-sinh" data-focus-group="sinh"><dt><label><input type="checkbox" id="toggle-sinh" class="visibility-toggle" data-target="sinh" checked><span class="color-swatch" style="background-color: var(--color-sinh);" aria-hidden="true"></span>sinh(p)</label></dt><dd id="val-sinh">1.000</dd></div>
                        <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--color-tan);" aria-hidden="true"></span>tanh(p)</dt><dd id="val-tanh">0.707</dd></div>
                        <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--color-p-area);" aria-hidden="true"></span>Area (p/2)</dt><dd id="val-area">0.441</dd></div>
                        <div class="info-item is-static"><dt><span class="color-swatch" style="background-color: var(--accent-color);" aria-hidden="true"></span>Parameter (p)</dt><dd id="val-p">0.881</dd></div>
                    </dl>
                </div>
            </div>
        </aside>
    </main>

    <footer class="container">
        <p>&copy; 2024 Interactive Trigonometry Explorer. All rights reserved.</p>
    </footer>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const svg = document.getElementById('trig-visualization');
            const slider = document.getElementById('angle-slider');

            const elements = {
                angleDisplay: document.getElementById('angle-display'),
                pointP: document.getElementById('point-P'),
                pointPHitbox: document.getElementById('point-P-hitbox'),
                pointH: document.getElementById('point-H'),
                lineRadius: document.getElementById('line-radius'),
                angleArc: document.getElementById('angle-arc'),
                angleLabel: document.getElementById('angle-label'),
                hyperbolaPath: document.getElementById('hyperbola-path'),
                hyperbolicElements: document.getElementById('hyperbolic-elements'),
                hyperbolicInfoGroup: document.getElementById('hyperbolic-info-group'),
                hyperbolicNotice: document.getElementById('hyperbolic-notice'),
                circularArea: document.getElementById('circular-area'),
                hyperbolicArea: document.getElementById('hyperbolic-area'),
                projectionLine: document.getElementById('projection-line'),
                animationLayer: document.getElementById('animation-layer'),
                trigLinesGroup: document.getElementById('trig-lines'),
                valSin: document.getElementById('val-sin'), valCos: document.getElementById('val-cos'),
                valTan: document.getElementById('val-tan'), valSec: document.getElementById('val-sec'),
                valCsc: document.getElementById('val-csc'), valCot: document.getElementById('val-cot'),
                valP: document.getElementById('val-p'), valSinh: document.getElementById('val-sinh'),
                valCosh: document.getElementById('val-cosh'), valTanh: document.getElementById('val-tanh'),
                valArea: document.getElementById('val-area'),
                demoToggleButton: document.getElementById('demo-toggle-button'),
                demoControls: document.getElementById('demo-controls'),
                demoExplanation: document.getElementById('demo-explanation'),
                demoProgress: document.getElementById('demo-progress'),
                demoPrev: document.getElementById('demo-prev'),
                demoNext: document.getElementById('demo-next'),
            };

            const ALL_FUNCTIONS = ['sin', 'cos', 'tan', 'sec', 'csc', 'cot', 'sinh', 'cosh'];
            ALL_FUNCTIONS.forEach(fn => {
                elements[`line-${fn}`] = document.getElementById(`line-${fn}`);
                elements[`label-${fn}`] = document.getElementById(`label-${fn}`);
                elements[`group-${fn}`] = document.getElementById(`group-${fn}`);
                elements[`toggle-${fn}`] = document.getElementById(`toggle-${fn}`);
                elements[`info-item-${fn}`] = document.getElementById(`info-item-${fn}`);
            });

            const SVG_BOUNDS = 2.2;
            const EPSILON = 1e-9;
            const ARC_RADIUS = 0.3;
            const LABEL_RADIUS = 0.4;
            const ANIMATION_SEGMENTS = 50;
            const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)').matches;

            const state = {
                currentDeg: parseFloat(slider.value),
                targetDeg: parseFloat(slider.value),
                animationFrameId: null,
                isDragging: false,
                visibility: {
                    sin: true, cos: true, tan: true, sec: true, csc: true, cot: true, sinh: true, cosh: true
                },
                demo: {
                    active: false,
                    step: 0,
                    values: {}
                }
            };

            function formatValue(val) {
                if (val === null || val === undefined || !isFinite(val)) {
                    if (val === Infinity) return '→ ∞';
                    if (val === -Infinity) return '→ −∞';
                    return 'undef';
                }
                return val.toFixed(3);
            }

            function generateHyperbolaPath(maxP, steps = 50) {
                let pathD_top = 'M 1 0 ';
                let pathD_bottom = 'M 1 0 ';
                for (let i = 1; i <= steps; i++) {
                    const p = (i / steps) * maxP;
                    const x = Math.cosh(p);
                    const y = Math.sinh(p);
                    if (x > SVG_BOUNDS) break;
                    pathD_top += `L ${x.toPrecision(5)} ${(-y).toPrecision(5)} `;
                    pathD_bottom += `L ${x.toPrecision(5)} ${y.toPrecision(5)} `;
                }
                return pathD_top + pathD_bottom;
            }

            function generateAreaPath(type, param, segments) {
                if (Math.abs(param) < EPSILON) return 'M 0,0 Z';
                let pathD = 'M 0,0 ';
                const isCircular = type === 'circular';
                const endPointX = isCircular ? Math.cos(param) : Math.cosh(param);
                const endPointY = isCircular ? Math.sin(param) : Math.sinh(param);
                pathD += `L ${isCircular ? 1 : endPointX} ${isCircular ? 0 : -endPointY} `;

                for (let i = 1; i <= segments; i++) {
                    const t = i / segments;
                    const currentP = t * param;
                    const x = isCircular ? Math.cos(currentP) : Math.cosh(currentP);
                    const y = isCircular ? Math.sin(currentP) : Math.sinh(currentP);
                    pathD += `L ${x.toPrecision(5)} ${(-y).toPrecision(5)} `;
                }
                pathD += 'Z';
                return pathD;
            }

            function updateVisibility() {
                for (const fn in state.visibility) {
                    const isVisible = state.visibility[fn];
                    if (elements[`group-${fn}`]) {
                        elements[`group-${fn}`].style.visibility = isVisible ? 'visible' : 'hidden';
                    }
                    if (elements[`info-item-${fn}`]) {
                        elements[`info-item-${fn}`].classList.toggle('is-hidden', !isVisible);
                    }
                }
            }

            function updateSvgElements(values) {
                const { rad, s, c, t, sec, csc, cot, Px, Py, p, Hx, Hy, isHyperbolicDefined } = values;

                elements.pointP.setAttribute('cx', Px);
                elements.pointP.setAttribute('cy', Py);
                elements.pointPHitbox.setAttribute('cx', Px);
                elements.pointPHitbox.setAttribute('cy', Py);
                elements.lineRadius.setAttribute('x2', Px);
                elements.lineRadius.setAttribute('y2', Py);

                const largeArcFlag = (state.currentDeg % 360) > 180 ? 1 : 0;
                const arcPath = `M ${ARC_RADIUS} 0 A ${ARC_RADIUS} ${ARC_RADIUS} 0 ${largeArcFlag} 0 ${ARC_RADIUS * c} ${-ARC_RADIUS * s}`;
                elements.angleArc.setAttribute('d', arcPath);
                elements.angleLabel.setAttribute('x', LABEL_RADIUS * Math.cos(rad / 2));
                elements.angleLabel.setAttribute('y', -LABEL_RADIUS * Math.sin(rad / 2));

                elements.circularArea.setAttribute('d', generateAreaPath('circular', rad, ANIMATION_SEGMENTS));

                const setLineAndLabel = (name, x1, y1, x2, y2) => {
                    elements[`line-${name}`].setAttribute('x1', x1);
                    elements[`line-${name}`].setAttribute('y1', y1);
                    elements[`line-${name}`].setAttribute('x2', x2);
                    elements[`line-${name}`].setAttribute('y2', y2);

                    const labelOffset = (name === 'cos' || name === 'cot' || name === 'cosh') ? 0.12 : -0.12;
                    const labelAngleRad = Math.atan2(y2 - y1, x2 - x1);
                    const lx = (x1 + x2) / 2 + labelOffset * Math.sin(labelAngleRad);
                    const ly = (y1 + y2) / 2 - labelOffset * Math.cos(labelAngleRad);
                    elements[`label-${name}`].setAttribute('transform', `translate(${lx}, ${ly})`);
                };

                setLineAndLabel('sin', Px, Py, Px, 0);
                setLineAndLabel('cos', Px, 0, 0, 0);
                setLineAndLabel('tan', 1, 0, 1, -t);
                setLineAndLabel('sec', 0, 0, 1, -t);
                setLineAndLabel('csc', 0, 0, cot, -1);
                setLineAndLabel('cot', cot, -1, 0, -1);

                elements.hyperbolicElements.style.visibility = isHyperbolicDefined ? 'visible' : 'hidden';
                if (isHyperbolicDefined) {
                    elements.pointH.setAttribute('cx', Hx);
                    elements.pointH.setAttribute('cy', Hy);
                    setLineAndLabel('cosh', 0, 0, Hx, 0);
                    setLineAndLabel('sinh', Hx, 0, Hx, Hy);
                    elements.hyperbolicArea.setAttribute('d', generateAreaPath('hyperbolic', p, ANIMATION_SEGMENTS));
                    elements.projectionLine.setAttribute('x1', 1);
                    elements.projectionLine.setAttribute('y1', -t);
                    elements.projectionLine.setAttribute('x2', Hx);
                    elements.projectionLine.setAttribute('y2', Hy);
                } else {
                    elements.hyperbolicArea.setAttribute('d', 'M 0,0 Z');
                }
            }

            function updateInfoPanel(values) {
                const { deg, rad, s, c, t, sec, csc, cot, p, sinh_p, cosh_p, tanh_p, isHyperbolicDefined } = values;

                const normDeg = (deg % 360 + 360) % 360;
                const normRad = (rad % (2 * Math.PI) + 2 * Math.PI) % (2 * Math.PI);
                elements.angleDisplay.textContent = `${normDeg.toFixed(1)}° / ${normRad.toFixed(3)} rad`;
                slider.setAttribute('aria-valuetext', `Angle: ${normDeg.toFixed(1)} degrees`);
                slider.style.setProperty('--slider-progress', `${(normDeg / 360) * 100}%`);

                const valueMap = { sin: s, cos: c, tan: t, sec: sec, csc: csc, cot: cot, p: p, sinh: sinh_p, cosh: cosh_p, tanh: tanh_p };

                for (const fnName in valueMap) {
                    const value = valueMap[fnName];
                    const valEl = elements[`val-${fnName}`];
                    if (valEl) valEl.textContent = formatValue(value);

                    const itemEl = elements[`info-item-${fnName}`];
                    if (itemEl) {
                        const isUndef = !isFinite(value);
                        itemEl.classList.toggle('is-undefined', isUndef);

                        const toggleEl = elements[`toggle-${fnName}`];
                        if (toggleEl) {
                            toggleEl.disabled = isUndef;
                            itemEl.setAttribute('aria-disabled', isUndef);
                        }
                    }
                }
                elements.valArea.textContent = formatValue(p / 2);

                elements.hyperbolicInfoGroup.setAttribute('aria-disabled', !isHyperbolicDefined);
                elements.hyperbolicNotice.hidden = isHyperbolicDefined;
                elements.demoToggleButton.disabled = !isHyperbolicDefined;
            }

            function calculateValues(deg) {
                const rad = deg * Math.PI / 180;
                let s = Math.sin(rad);
                let c = Math.cos(rad);
                let t, sec, csc, cot;

                if (Math.abs(c) < EPSILON) {
                    c = 0; t = s > 0 ? Infinity : -Infinity; sec = t; csc = 1 / s; cot = 0;
                } else if (Math.abs(s) < EPSILON) {
                    s = 0; t = 0; sec = 1 / c; csc = c > 0 ? Infinity : -Infinity; cot = csc;
                } else {
                    t = s / c; sec = 1 / c; csc = 1 / s; cot = c / s;
                }

                const Px = c;
                const Py = -s;

                let p = NaN, sinh_p = NaN, cosh_p = NaN, tanh_p = NaN, Hx = NaN, Hy = NaN;
                const isHyperbolicDefined = sec >= 1 - EPSILON && isFinite(sec);

                if (isHyperbolicDefined) {
                    p = Math.acosh(sec);
                    sinh_p = Math.sinh(p);
                    cosh_p = Math.cosh(p);
                    tanh_p = Math.tanh(p);
                    Hx = cosh_p;
                    Hy = -sinh_p;
                }

                return { deg, rad, s, c, t, sec, csc, cot, Px, Py, p, sinh_p, cosh_p, tanh_p, Hx, Hy, isHyperbolicDefined };
            }

            function render(deg) {
                const values = calculateValues(deg);
                updateSvgElements(values);
                updateInfoPanel(values);
            }

            function animationLoop() {
                const diff = state.targetDeg - state.currentDeg;
                if (prefersReducedMotion || Math.abs(diff) < 0.05) {
                    state.currentDeg = state.targetDeg;
                    cancelAnimationFrame(state.animationFrameId);
                    state.animationFrameId = null;
                } else {
                    state.currentDeg += diff * 0.15; // Easing
                    state.animationFrameId = requestAnimationFrame(animationLoop);
                }
                render(state.currentDeg);
            }

            function startAnimation() {
                if (!state.animationFrameId) {
                    state.animationFrameId = requestAnimationFrame(animationLoop);
                }
            }

            function handleFocus(event) {
                const infoItem = event.currentTarget;
                if (!infoItem || state.demo.active) return;
                const focusGroup = infoItem.dataset.focusGroup;
                if (!focusGroup || elements[`toggle-${focusGroup}`]?.checked === false) return;

                elements.trigLinesGroup.classList.add('in-focus-mode');
                elements.hyperbolicElements.classList.add('in-focus-mode');

                document.querySelectorAll(`[data-focus-group="${focusGroup}"]`).forEach(el => el.classList.add('is-focused'));

                if (['sin', 'cos', 'tan', 'sec', 'csc', 'cot'].includes(focusGroup)) {
                    elements.lineRadius.classList.add('is-focused');
                }
            }

            function handleBlur() {
                if (state.demo.active) return;
                elements.trigLinesGroup.classList.remove('in-focus-mode');
                elements.hyperbolicElements.classList.remove('in-focus-mode');
                document.querySelectorAll('.is-focused').forEach(el => el.classList.remove('is-focused'));
            }

            const demoSteps = [
                {
                    title: "Introduction",
                    description: "This demo shows how <strong>sec(θ)</strong> and <strong>tan(θ)</strong> from the unit circle relate to <strong>cosh(p)</strong> and <strong>sinh(p)</strong> on the unit hyperbola. The key is that the circular sector area (θ/2) equals the hyperbolic sector area (p/2)."
                },
                {
                    title: "Identify sec(θ) and tan(θ)",
                    description: "On the unit circle, the line tangent at (1,0) gives us <strong>tan(θ)</strong>. The line from the origin to this point on the tangent is <strong>sec(θ)</strong>.",
                    action: (v) => {
                        elements['group-sec'].classList.add('highlight-pulse');
                        elements['group-tan'].classList.add('highlight-pulse');
                    }
                },
                {
                    title: "Transform sec(θ) to cosh(p)",
                    description: "The length of <strong>sec(θ)</strong> is equal to <strong>cosh(p)</strong>. This animation moves the secant line to the x-axis to represent cosh(p).",
                    action: (v) => {
                        const secClone = elements['line-sec'].cloneNode(true);
                        secClone.id = ''; secClone.classList.add('animating-clone');
                        elements.animationLayer.appendChild(secClone);
                        requestAnimationFrame(() => {
                            secClone.setAttribute('transform', `rotate(${-v.deg} 0 0)`);
                            secClone.style.stroke = 'var(--color-cosh)';
                        });
                    }
                },
                {
                    title: "Transform tan(θ) to sinh(p)",
                    description: "Similarly, the length of <strong>tan(θ)</strong> is equal to <strong>sinh(p)</strong>. This animation moves the tangent line to its position on the hyperbola.",
                    action: (v) => {
                        const tanClone = elements['line-tan'].cloneNode(true);
                        tanClone.id = ''; tanClone.classList.add('animating-clone');
                        elements.animationLayer.appendChild(tanClone);
                        requestAnimationFrame(() => {
                            tanClone.setAttribute('transform', `translate(${v.Hx - 1} 0)`);
                            tanClone.style.stroke = 'var(--color-sinh)';
                        });
                    }
                },
                {
                    title: "Morph Areas",
                    description: "Crucially, the area of the circular sector shown is <strong>θ/2</strong>. This is numerically equal to the hyperbolic sector area, <strong>p/2</strong>. This equality is the foundation of the geometric relationship.",
                    action: (v) => {
                        const areaClone = elements['circular-area'].cloneNode(true);
                        areaClone.id = ''; areaClone.classList.add('animating-clone');
                        areaClone.style.fill = 'var(--color-p-area)';
                        elements.animationLayer.appendChild(areaClone);
                        const finalAreaD = generateAreaPath('hyperbolic', v.p, ANIMATION_SEGMENTS);
                        requestAnimationFrame(() => {
                            areaClone.setAttribute('d', finalAreaD);
                        });
                    }
                }
            ];

            function setDemoStep(index) {
                state.demo.step = index;
                elements.animationLayer.innerHTML = '';
                document.querySelectorAll('.highlight-pulse').forEach(el => el.classList.remove('highlight-pulse'));

                const step = demoSteps[index];
                elements.demoExplanation.innerHTML = step.description;
                elements.demoProgress.value = index;
                elements.demoPrev.disabled = index === 0;
                elements.demoNext.disabled = index === demoSteps.length - 1;

                if (step.action) {
                    step.action(state.demo.values);
                }
            }

            function toggleDemo() {
                state.demo.active = !state.demo.active;
                if (state.demo.active) {
                    state.demo.values = calculateValues(state.currentDeg);
                    elements.demoToggleButton.textContent = "End Demo";
                    elements.demoToggleButton.setAttribute('aria-pressed', 'true');
                    elements.demoControls.hidden = false;
                    slider.disabled = true;
                    svg.style.cursor = 'default';
                    handleBlur();
                    setDemoStep(0);
                } else {
                    elements.demoToggleButton.textContent = "Start Interactive Demo";
                    elements.demoToggleButton.setAttribute('aria-pressed', 'false');
                    elements.demoControls.hidden = true;
                    slider.disabled = false;
                    svg.style.cursor = 'grab';
                    elements.animationLayer.innerHTML = '';
                    document.querySelectorAll('.highlight-pulse').forEach(el => el.classList.remove('highlight-pulse'));
                }
            }

            function getSVGPoint(event) {
                const pt = svg.createSVGPoint();
                const touch = event.touches ? event.touches[0] : event;
                pt.x = touch.clientX;
                pt.y = touch.clientY;
                return pt.matrixTransform(svg.getScreenCTM().inverse());
            }

            function handleDragStart(e) {
                if (state.demo.active) return;
                e.preventDefault();
                state.isDragging = true;
                svg.style.cursor = 'grabbing';
                document.body.style.cursor = 'grabbing';
                handleDragMove(e);
            }

            function handleDragMove(e) {
                if (!state.isDragging) return;
                e.preventDefault();
                const { x, y } = getSVGPoint(e);
                let angleRad = Math.atan2(-y, x);
                const newDeg = (angleRad * 180 / Math.PI + 360) % 360;

                state.currentDeg = newDeg;
                state.targetDeg = newDeg;
                slider.value = newDeg;
                render(newDeg);
            }

            function handleDragEnd(e) {
                if (!state.isDragging) return;
                e.preventDefault();
                state.isDragging = false;
                svg.style.cursor = 'grab';
                document.body.style.cursor = '';
            }

            function initialize() {
                const maxP = Math.acosh(SVG_BOUNDS);
                elements.hyperbolaPath.setAttribute('d', generateHyperbolaPath(maxP));

                slider.addEventListener('input', () => {
                    state.targetDeg = parseFloat(slider.value);
                    if (!state.isDragging) startAnimation();
                });

                document.querySelectorAll('.info-item[data-focus-group]').forEach(item => {
                    item.addEventListener('mouseenter', handleFocus);
                    item.addEventListener('mouseleave', handleBlur);
