            const pauseIconSVG = '<path d="M6 19h4V5H6v14zm8-14v14h4V5h-4z"></path>';

            // --- UTILITY FUNCTIONS ---
            const format = (num) => (num === undefined || isNaN(num)) ? '---' : num.toFixed(3);
