                dom.data.sin.textContent = format(sin_t);
                dom.data.cosh.textContent = format(cosh_p);
                dom.data.sinh.textContent = format(sinh_p);
                dom.data.tanh.textContent = format(tanh_p);

                // Define key points (Y inverted for SVG)
                const O = { x: 0, y: 0 };
                const P_circle = { x: cos_t, y: -sin_t };
                const P_cos_axis = { x: cos_t, y: 0 };
                const P_tan_base = { x: 1, y: 0 };
                const P_tan_tip = { x: 1, y: -tan_t };
                const P_hyperbola = { x: sec_t, y: -tan_t };
                const P_sec_axis = { x: sec_t, y: 0 };

                // Update SVG elements
                setPoint(dom.svg.points.circle, P_circle);
                setPoint(dom.svg.points.tangent, P_tan_tip);
                setPoint(dom.svg.points.hyperbola, P_hyperbola);

                setLine(dom.svg.lines.cos, O, P_cos_axis);
                setLine(dom.svg.lines.sin, P_cos_axis, P_circle);
                setLine(dom.svg.lines.tan, P_tan_base, P_tan_tip);
                setLine(dom.svg.lines.sec, O, P_sec_axis);
                setLine(dom.svg.lines.cosh, O, P_sec_axis);
                setLine(dom.svg.lines.sinh, P_sec_axis, P_hyperbola);

                setLine(dom.svg.guidelines.radial, O, P_hyperbola);
                setLine(dom.svg.guidelines.tan, P_circle, P_tan_tip);
                setLine(dom.svg.guidelines.hyperbola, P_sec_axis, P_hyperbola);
                setLine(dom.svg.guidelines.connector, P_tan_tip, P_hyperbola);

                const arcRadius = 0.3;
                const arc_p2 = { x: arcRadius * cos_t, y: -arcRadius * sin_t };
                const largeArcFlag = Math.abs(deg) > 180 ? 1 : 0;
                const sweepFlag = deg >= 0 ? 1 : 0;
                dom.svg.arc.setAttribute('d', `M ${arcRadius} 0 A ${arcRadius} ${arcRadius} 0 ${largeArcFlag} ${sweepFlag} ${arc_p2.x} ${arc_p2.y} L 0 0 Z`);

                // Update Labels
                const ySign = clampedTheta >= 0 ? -1 : 1;
                const labelOffset = 0.08;
                dom.svg.labels.theta.setAttribute('x', arcRadius * 1.2 * Math.cos(clampedTheta/2));
                dom.svg.labels.theta.setAttribute('y', -arcRadius * 1.2 * Math.sin(clampedTheta/2));

                dom.svg.labels.cos.setAttribute('x', P_cos_axis.x / 2);
                dom.svg.labels.cos.setAttribute('y', ySign * labelOffset * 1.5);
                dom.svg.labels.sin.setAttribute('x', P_circle.x + labelOffset * ySign * (P_circle.x < 0 ? -1: 1));
                dom.svg.labels.sin.setAttribute('y', P_circle.y / 2);

                dom.svg.labels.tan.setAttribute('x', P_tan_tip.x + labelOffset);
                dom.svg.labels.tan.setAttribute('y', P_tan_tip.y / 2);
                dom.svg.labels.sinh.setAttribute('x', P_hyperbola.x + labelOffset);
                dom.svg.labels.sinh.setAttribute('y', P_hyperbola.y / 2);

                dom.svg.labels.sec.setAttribute('x', P_sec_axis.x / 2);
                dom.svg.labels.sec.setAttribute('y', -labelOffset * 0.8);
                dom.svg.labels.cosh.setAttribute('x', P_sec_axis.x / 2);
                dom.svg.labels.cosh.setAttribute('y', labelOffset * 1.5);
            }

            // --- ANIMATION LOOP ---
            function playLoop() {
                if (!isPlaying) return;
                let currentVal = parseFloat(dom.slider.value);
                let newVal = currentVal + 0.2;
                if (newVal > dom.slider.max) newVal = parseFloat(dom.slider.min);
                dom.slider.value = newVal;
                updateVisualization(newVal);
                animationFrameId = requestAnimationFrame(playLoop);
            }

            function togglePlay() {
                isPlaying = !isPlaying;
                if (isPlaying) {
                    dom.playPauseBtn.setAttribute('aria-label', 'Pause animation');
                    dom.playPauseIcon.innerHTML = pauseIconSVG;
                    dom.playPauseText.textContent = 'Pause';
                    animationFrameId = requestAnimationFrame(playLoop);
                } else {
                    if (animationFrameId) cancelAnimationFrame(animationFrameId);
                    animationFrameId = null;
                    dom.playPauseBtn.setAttribute('aria-label', 'Play animation');
                    dom.playPauseIcon.innerHTML = playIconSVG;
                    dom.playPauseText.textContent = 'Play';
                }
            }

            // --- ONE-TIME SETUP ---
            function setup() {
                // Generate grid
                const svgNS = 'http://www.w3.org/2000/svg';
                for (let i = -2; i <= 2; i++) {
                    if (i === 0) continue;
                    const vLine = document.createElementNS(svgNS, 'line');
                    setLine(vLine, {x: i, y: -2.5}, {x: i, y: 2.5});
                    vLine.setAttribute('class', 'svg-grid-line');
                    dom.svg.grid.appendChild(vLine);
                    const hLine = document.createElementNS(svgNS, 'line');
                    setLine(hLine, {x: -2.5, y: i}, {x: 2.5, y: i});
                    hLine.setAttribute('class', 'svg-grid-line');
                    dom.svg.grid.appendChild(hLine);
                }

                // Generate hyperbola path
                const maxP = 1.7; const steps = 50; let pathData = '';
                for (let i = steps; i >= -steps; i--) {
                    const p = (i / steps) * maxP;
                    const x = Math.cosh(p); const y = Math.sinh(p);
                    pathData += `${i === steps ? 'M' : 'L'} ${x} ${-y} `;
                }
                dom.svg.unitHyperbola.setAttribute('d', pathData);

                // Add event listeners
                dom.slider.addEventListener('input', (e) => updateVisualization(e.target.value));
                dom.playPauseBtn.addEventListener('click', togglePlay);

                // Initial render
                updateVisualization(dom.slider.value);
            }

            setup();
        });
