            transition: background-color 0.2s ease, transform 0.1s ease;
            display: flex; align-items: center; justify-content: center; gap: 0.5rem;
        }
        .play-pause-btn:hover { background-color: var(--accent-color); }
        .play-pause-btn:active { transform: scale(0.98); }
        .play-pause-btn:focus-visible { outline: 2px solid var(--accent-color); outline-offset: 2px; }

        .data-panel .data-grid {
            display: grid;
            grid-template-columns: 1fr;
            gap: 1.25rem;
            margin-top: 1.25rem;
        }

        .data-group h3 {
            font-size: 1rem; font-weight: 600;
            color: var(--text-color); margin-bottom: 0.5rem;
        }

        .data-list {
            display: grid; grid-template-columns: auto 1fr;
            gap: 0.5rem 1rem; font-family: var(--font-mono);
            font-size: 0.95rem;
        }

        .data-list dt, .data-list dd {
            padding: 0.2rem 0.4rem;
            border-radius: 4px;
            transition: background-color 0.2s ease;
        }

        .data-list dt { color: var(--text-muted-color); text-align: right; white-space: nowrap; }
        .data-list dd { font-weight: 500; color: var(--text-color); overflow-wrap: break-word; }

        .dd-cos { color: var(--primary-color); }
        .dd-sin { color: var(--secondary-color); }
        .dd-sec, .dd-cosh { color: var(--red-color); }
        .dd-tan, .dd-sinh { color: var(--green-color); }
        .dd-p { color: var(--accent-color); }
        .dd-area-circ { color: var(--primary-color); }
        .dd-area-hyper { color: var(--accent-color); }

        .connection-highlight {
            font-weight: 700;
            background-color: color-mix(in srgb, var(--red-color) 20%, transparent);
            padding: 0.1rem 0.4rem; border-radius: 4px;
        }
        .connection-highlight.green { background-color: color-mix(in srgb, var(--green-color) 20%, transparent); }

        .explanation-panel summary {
            cursor: pointer; font-weight: 600; list-style-position: inside;
            color: var(--secondary-color); font-size: 1.25rem;
        }
        .explanation-panel summary:hover { color: var(--accent-color); }
        .explanation-panel[open] summary { margin-bottom: 1rem; }
        .explanation-panel p { margin-bottom: 1em; color: var(--text-muted-color); }
        .explanation-panel p:last-child { margin-bottom: 0; }
        .explanation-panel code {
            font-family: var(--font-mono); font-size: 0.9em;
            background-color: var(--bg-color); padding: 0.2rem 0.4rem;
            border-radius: 4px; color: var(--accent-color);
        }

        /* SVG Styles */
        .svg-bg { fill: none; }
        .svg-grid-line { stroke: var(--border-color); stroke-width: 0.01; }
        .svg-axis { stroke: var(--text-muted-color); stroke-width: 0.02; }
        .svg-unit-circle { stroke: var(--primary-color); stroke-width: 0.03; fill: none; opacity: 0.5; }
        .svg-unit-hyperbola { stroke: var(--secondary-color); stroke-width: 0.03; fill: none; stroke-dasharray: 0.1, 0.1; opacity: 0.5; }

        .svg-guideline { stroke: var(--text-muted-color); stroke-width: 0.015; stroke-dasharray: 0.05, 0.05; }
        .svg-arc-theta { fill: var(--primary-color); opacity: 0.2; }
        .svg-area-hyperbolic { fill: var(--accent-color); opacity: 0.2; }

        .svg-dynamic-line { transition: opacity 0.2s ease-out; }
        .svg-point { fill: var(--accent-color); stroke: var(--bg-color); stroke-width: 0.03; r: 0.06; transition: fill 0.2s ease; }
        #svg-point-circle { cursor: grab; }
        #svg-point-circle:active { cursor: grabbing; }

        .svg-label, .svg-axis-label {
            font-family: var(--font-mono);
            fill: var(--text-color); paint-order: stroke;
            stroke: var(--surface-color); stroke-width: 0.04;
            stroke-linejoin: round; pointer-events: none;
        }
        .svg-label { font-size: 0.15; }
        .svg-axis-label { font-size: 0.12; fill: var(--text-muted-color); }

        .svg-label-cos { fill: var(--primary-color); }
        .svg-label-sin { fill: var(--secondary-color); }
        .svg-label-sec-cosh { fill: var(--red-color); }
        .svg-label-tan-sinh { fill: var(--green-color); }
        .svg-label-theta { fill: var(--primary-color); }

        @keyframes pulse-highlight { 50% { stroke-width: 0.07; } }
        .is-highlighted-svg { animation: pulse-highlight 0.3s ease-out; stroke-width: 0.07; }
        .is-highlighted-data { background-color: var(--highlight-bg); }

        footer {
            text-align: center; padding: 1.5rem; margin-top: 2rem;
            border-top: 1px solid var(--border-color);
            color: var(--text-muted-color); font-size: 0.9rem;
        }

        @media (max-width: 1024px) {
            .layout-grid { grid-template-columns: 1fr; }
        }

        @media (min-width: 768px) {
             .data-panel .data-grid {
                grid-template-columns: 1fr 1fr;
                gap: 1.25rem 2rem;
            }
        }

        @media (max-width: 640px) {
            .container { padding: 1rem; }
            .panel { padding: 1rem; }
            .angle-input-wrapper { flex-direction: column; align-items: stretch; }
            #angle-input { width: 100%; text-align: center; }
        }
    </style>
</head>
<body>
    <div class="container">
        <header>
            <h1>Interactive Unit Circle & Hyperbola</h1>
            <p>Explore the profound connection between circular and hyperbolic trigonometry. Drag the point on the circle or use the slider to see how <code class="connection-highlight">sec(θ) = cosh(p)</code> and <code class="connection-highlight green">tan(θ) = sinh(p)</code>.</p>
        </header>

        <main>
            <div class="layout-grid">
                <figure class="visualization-container" role="group" aria-labelledby="viz-caption">
                    <svg id="geometry-svg" viewBox="-3 -3 6 6" preserveAspectRatio="xMidYMid meet">
                        <title>Unit Circle and Hyperbola Visualization</title>
                        <desc>An interactive diagram where a changing angle θ on a unit circle controls a point on a unit hyperbola. Lines representing sec(θ) and cosh(p) are shown to be equal, as are tan(θ) and sinh(p). The corresponding circular and hyperbolic sector areas are also visualized.</desc>

                        <defs>
                            <marker id="arrowhead" viewBox="0 0 10 10" refX="5" refY="5" markerWidth="6" markerHeight="6" orient="auto-start-reverse" fill="var(--text-muted-color)">
                                <path d="M 0 0 L 10 5 L 0 10 z" />
                            </marker>
                        </defs>

                        <rect class="svg-bg" x="-3" y="-3" width="6" height="6" />
                        <g id="svg-grid"></g>
                        <path id="svg-unit-hyperbola" class="svg-unit-hyperbola"></path>
                        <circle class="svg-unit-circle" cx="0" cy="0" r="1" />

                        <line class="svg-axis" x1="-3" y1="0" x2="3" y2="0" marker-end="url(#arrowhead)" />
                        <line class="svg-axis" x1="0" y1="3" x2="0" y2="-3" marker-end="url(#arrowhead)" />

                        <g id="svg-axis-labels" text-anchor="middle">
                            <text class="svg-axis-label" x="1" y="-0.15">1</text>
                            <text class="svg-axis-label" x="2" y="-0.15">2</text>
                            <text class="svg-axis-label" x="-1" y="-0.15">-1</text>
                            <text class="svg-axis-label" x="-2" y="-0.15">-2</text>
                            <text class="svg-axis-label" x="-0.15" y="-1" dominant-baseline="central">1</text>
                            <text class="svg-axis-label" x="-0.15" y="-2" dominant-baseline="central">2</text>
                            <text class="svg-axis-label" x="-0.15" y="1" dominant-baseline="central">-1</text>
                            <text class="svg-axis-label" x="-0.15" y="2" dominant-baseline="central">-2</text>
                        </g>

                        <g id="svg-dynamic-elements">
                            <path id="svg-arc-theta" class="svg-arc-theta"></path>
                            <path id="svg-area-hyperbolic" class="svg-area-hyperbolic"></path>

                            <line id="svg-guideline-radial" class="svg-guideline" />
                            <line id="svg-guideline-tan" class="svg-guideline" />
                            <line id="svg-guideline-hyperbola" class="svg-guideline" />

                            <line id="svg-line-cos" class="svg-dynamic-line svg-line-cos" data-highlight="cos-group" />
                            <line id="svg-line-sin" class="svg-dynamic-line svg-line-sin" data-highlight="sin-group" />
                            <line id="svg-line-tan-sinh" class="svg-dynamic-line svg-line-tan-sinh" data-highlight="tan-sinh-group" />
                            <line id="svg-line-sec-cosh" class="svg-dynamic-line svg-line-sec-cosh" data-highlight="sec-cosh-group" />

                            <circle id="svg-point-circle" class="svg-point" />
                            <circle id="svg-point-tangent" class="svg-point" />
                            <circle id="svg-point-hyperbola" class="svg-point" />

                            <text id="svg-label-theta" class="svg-label svg-label-theta" text-anchor="middle">θ</text>
                            <text id="svg-label-cos" class="svg-label svg-label-cos" text-anchor="middle">cos</text>
                            <text id="svg-label-sin" class="svg-label svg-label-sin" text-anchor="middle">sin</text>
                            <text id="svg-label-tan-sinh" class="svg-label svg-label-tan-sinh" text-anchor="start">tan=sinh</text>
                            <text id="svg-label-sec-cosh" class="svg-label svg-label-sec-cosh" text-anchor="middle">sec=cosh</text>
                        </g>
                    </svg>
                    <figcaption id="viz-caption">Drag the point on the circle, or use the controls.</figcaption>
                </figure>

                <aside class="sidebar">
                    <section class="panel controls-panel" aria-labelledby="controls-heading">
                        <h2 id="controls-heading">Controls</h2>
                        <div class="controls-group">
                            <div class="control-item">
                                <label for="angle-slider">Angle (θ)</label>
                                <div class="angle-input-wrapper">
                                    <input type="number" id="angle-input" value="45.0" min="-89.9" max="89.9" step="0.1" aria-label="Precise angle in degrees">
                                    <input type="range" id="angle-slider" name="angle" min="-89.9" max="89.9" value="45" step="0.1" aria-label="Angle slider">
                                </div>
                            </div>
                            <div class="control-item">
                                <label for="speed-slider">Animation Speed</label>
                                <input type="range" id="speed-slider" min="0.1" max="1.5" value="0.5" step="0.1" aria-label="Animation speed">
                            </div>
                             <button id="play-pause-btn" class="play-pause-btn" aria-label="Play animation">
                                <svg id="play-icon" width="18" height="18" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true"><path d="M8 5v14l11-7z"></path></svg>
                                <svg id="pause-icon" width="18" height="18" viewBox="0 0 24 24" fill="currentColor" style="display: none;" aria-hidden="true"><path d="M6 19h4V5H6v14zm8-14v14h4V5h-4z"></path></svg>
                                <span id="play-pause-text">Play</span>
                            </button>
                        </div>
                    </section>

                    <details class="panel explanation-panel" id="explanation">
                        <summary>What am I looking at?</summary>
                        <p>This visualization reveals the deep link between circular functions (like <code>tan</code>, <code>sec</code>) and hyperbolic functions (<code>sinh</code>, <code>cosh</code>) through the <strong>Gudermannian function</strong>.</p>
                        <p>The angle <code>θ</code> on the unit circle defines a parameter <code>p</code> on the hyperbola such that <code>sec(θ) = cosh(p)</code> and <code>tan(θ) = sinh(p)</code>. This parameter <code>p</code> is the result of the Gudermannian function applied to <code>θ</code>.</p>
                        <p>Crucially, the area of the circular sector (in blue/green) is <code>θ/2</code>, and the area of the hyperbolic sector (in yellow/orange) is <code>p/2</code>. This geometric relationship underscores their connection.</p>
                    </details>

                    <section class="panel data-panel" aria-labelledby="data-heading" aria-live="polite" aria-atomic="true">
                        <h2 id="data-heading">Live Values</h2>
                        <div class="data-grid">
                            <div class="data-group">
                                <h3>Circular Functions</h3>
                                <dl class="data-list">
                                    <dt data-highlight="sec-cosh-group">sec(θ) =</dt>
                                    <dd id="val-sec" class="dd-sec connection-highlight" data-highlight="sec-cosh-group">1.414</dd>
                                    <dt data-highlight="tan-sinh-group">tan(θ) =</dt>
                                    <dd id="val-tan" class="dd-tan connection-highlight green" data-highlight="tan-sinh-group">1.000</dd>
                                    <dt data-highlight="cos-group">cos(θ) =</dt>
                                    <dd id="val-cos" class="dd-cos" data-highlight="cos-group">0.707</dd>
                                    <dt data-highlight="sin-group">sin(θ) =</dt>
                                    <dd id="val-sin" class="dd-sin" data-highlight="sin-group">0.707</dd>
                                </dl>
                            </div>
                            <div class="data-group">
                                <h3>Hyperbolic Functions</h3>
                                <dl class="data-list">
                                    <dt data-highlight="sec-cosh-group">cosh(p) =</dt>
                                    <dd id="val-cosh" class="dd-cosh connection-highlight" data-highlight="sec-cosh-group">1.414</dd>
                                    <dt data-highlight="tan-sinh-group">sinh(p) =</dt>
                                    <dd id="val-sinh" class="dd-sinh connection-highlight green" data-highlight="tan-sinh-group">1.000</dd>
                                    <dt data-highlight="p-group">p =</dt>
                                    <dd id="val-p" class="dd-p" data-highlight="p-group">0.881</dd>
                                </dl>
                            </div>
                             <div class="data-group">
                                <h3>Sector Areas</h3>
                                <dl class="data-list">
                                    <dt data-highlight="area-circ-group">θ/2 =</dt>
                                    <dd id="val-area-circular" class="dd-area-circ" data-highlight="area-circ-group">0.393</dd>
                                    <dt data-highlight="area-hyper-group">p/2 =</dt>
                                    <dd id="val-area-hyperbolic" class="dd-area-hyper" data-highlight="area-hyper-group">0.441</dd>
                                </dl>
                            </div>
                        </div>
                    </section>
                </aside>
            </div>
        </main>
        <footer>
            <p>Interactive Visualization &copy; 2024. A demonstration of robust web engineering.</p>
        </footer>
    </div>

    <script>
        'use strict';

        document.addEventListener('DOMContentLoaded', () => {
            // --- CORE MATHEMATICAL CONTEXT ---
            // This script visualizes the relationship between circular and hyperbolic trigonometry
            // via the Gudermannian function, where `p = gd(θ)`.
            // Key identities shown:
            // sec(θ) = cosh(p)
            // tan(θ) = sinh(p)
            // The area of the circular sector is θ/2, and the hyperbolic sector area is p/2.

            // --- STATE & CONSTANTS ---
            let isPlaying = false;
            let wasPlayingBeforeDrag = false;
            let isDragging = false;
            let animationFrameId = null;
            let animationDirection = 1;
            const RAD_TO_DEG = 180 / Math.PI;
            const DEG_TO_RAD = Math.PI / 180;
            const ASYMPTOTE_THRESHOLD = 89.9;

            // --- DOM ELEMENTS CACHE ---
            const dom = {
                svg: document.getElementById('geometry-svg'),
                slider: document.getElementById('angle-slider'),
                angleInput: document.getElementById('angle-input'),
                speedSlider: document.getElementById('speed-slider'),
                playPauseBtn: document.getElementById('play-pause-btn'),
                playIcon: document.getElementById('play-icon'),
                pauseIcon: document.getElementById('pause-icon'),
                playPauseText: document.getElementById('play-pause-text'),
                layoutGrid: document.querySelector('.layout-grid'),
                data: {
                    p: document.getElementById('val-p'), sec: document.getElementById('val-sec'),
                    tan: document.getElementById('val-tan'), cos: document.getElementById('val-cos'),
                    sin: document.getElementById('val-sin'), cosh: document.getElementById('val-cosh'),
                    sinh: document.getElementById('val-sinh'),
                    areaCircular: document.getElementById('val-area-circular'),
                    areaHyperbolic: document.getElementById('val-area-hyperbolic'),
                },
                svgElements: {
                    grid: document.getElementById('svg-grid'),
                    unitHyperbola: document.getElementById('svg-unit-hyperbola'),
                    arcTheta: document.getElementById('svg-arc-theta'),
                    areaHyperbolic: document.getElementById('svg-area-hyperbolic'),
                    guidelines: {
                        radial: document.getElementById('svg-guideline-radial'),
                        tan: document.getElementById('svg-guideline-tan'),
                        hyperbola: document.getElementById('svg-guideline-hyperbola'),
                    },
                    lines: {
                        cos: document.getElementById('svg-line-cos'), sin: document.getElementById('svg-line-sin'),
                        tanSinh: document.getElementById('svg-line-tan-sinh'), secCosh: document.getElementById('svg-line-sec-cosh'),
                    },
                    points: {
                        circle: document.getElementById('svg-point-circle'), tangent: document.getElementById('svg-point-tangent'),
                        hyperbola: document.getElementById('svg-point-hyperbola'),
                    },
                    labels: {
                        theta: document.getElementById('svg-label-theta'), cos: document.getElementById('svg-label-cos'),
                        sin: document.getElementById('svg-label-sin'), tanSinh: document.getElementById('svg-label-tan-sinh'),
                        secCosh: document.getElementById('svg-label-sec-cosh'),
                    }
                }
            };

            // --- UTILITY FUNCTIONS ---
            const format = (num) => (num === undefined || isNaN(num)) ? '---' : num.toFixed(3);
            const setAttr = (el, attrs) => Object.entries(attrs).forEach(([key, value]) => el.setAttribute(key, value));
            const clamp = (num, min, max) => Math.min(Math.max(num, min), max);

            // --- CORE LOGIC ---
            function updateAll(degrees) {
                const angleDegrees = clamp(parseFloat(degrees) || 0, -ASYMPTOTE_THRESHOLD, ASYMPTOTE_THRESHOLD);
                const angleStr = angleDegrees.toFixed(1);

                if (dom.slider.value !== String(angleDegrees)) dom.slider.value = angleDegrees;
                if (dom.angleInput.value !== angleStr) dom.angleInput.value = angleStr;

                updateVisualization(angleDegrees);
            }

            function updateLabelPositions(theta, points) {
                const { cos_t, sin_t } = points;
                const ySign = theta >= 0 ? 1 : -1;
                const isNearVertical = Math.abs(cos_t) < 0.5;
                const isNearHorizontal = Math.abs(sin_t) < 0.3;

                // Theta Label
                setAttr(dom.svgElements.labels.theta, { x: 0.4 * Math.cos(theta / 2), y: -0.4 * Math.sin(theta / 2) });

                // Cos Label
                const cosLabelYOffset = isNearVertical ? ySign * 0.20 : ySign * 0.15;
                setAttr(dom.svgElements.labels.cos, { x: points.P_cos_axis.x / 2, y: cosLabelYOffset });

                // Sin Label
                const sinLabelXOffset = isNearVertical ? Math.sign(cos_t || 1) * 0.2 : Math.sign(cos_t || 1) * 0.12;
                setAttr(dom.svgElements.labels.sin, { x: points.P_circle.x + sinLabelXOffset, y: points.P_circle.y / 2 });
                dom.svgElements.labels.sin.setAttribute('text-anchor', cos_t > 0 ? 'start' : 'end');

                // Tan/Sinh Label
                const tanSinhLabelXOffset = isNearHorizontal ? 0.35 : 0.12;
                setAttr(dom.svgElements.labels.tanSinh, { x: points.P_hyperbola.x + (cos_t > 0 ? tanSinhLabelXOffset : -tanSinhLabelXOffset), y: points.P_hyperbola.y / 2 });
                dom.svgElements.labels.tanSinh.setAttribute('text-anchor', cos_t > 0 ? 'start' : 'end');

                // Sec/Cosh Label
                setAttr(dom.svgElements.labels.secCosh, { x: points.P_sec_axis.x / 2, y: -0.15 });
            }

            function updateVisualization(angleDegrees) {
                const isAtAsymptote = Math.abs(angleDegrees) >= ASYMPTOTE_THRESHOLD;
                const theta = angleDegrees * DEG_TO_RAD;

                const cos_t = Math.cos(theta);
                const sin_t = Math.sin(theta);
                const tan_t = isAtAsymptote ? Infinity : Math.tan(theta);
                const sec_t = isAtAsymptote ? Infinity : 1 / cos_t;

                const p = Math.asinh(tan_t);
                const cosh_p = Math.cosh(p);
                const sinh_p = Math.sinh(p);

                // Update Data Panel
                dom.slider.setAttribute('aria-valuetext', `${angleDegrees.toFixed(1)} degrees`);
                dom.data.p.textContent = isFinite(p) ? format(p) : '∞';
                dom.data.sec.textContent = isFinite(sec_t) ? format(sec_t) : '∞';
                dom.data.tan.textContent = isFinite(tan_t) ? format(tan_t) : '∞';
                dom.data.cos.textContent = format(cos_t);
                dom.data.sin.textContent = format(sin_t);
                dom.data.cosh.textContent = isFinite(cosh_p) ? format(cosh_p) : '∞';
                dom.data.sinh.textContent = isFinite(sinh_p) ? format(sinh_p) : '∞';
                dom.data.areaCircular.textContent = format(Math.abs(theta / 2));
                dom.data.areaHyperbolic.textContent = isFinite(p) ? format(Math.abs(p / 2)) : '∞';

                // Define key points (Y inverted for SVG coord system)
                const points = {
                    P_circle: { x: cos_t, y: -sin_t },
                    P_cos_axis: { x: cos_t, y: 0 },
                    P_tan_tip: { x: 1, y: -tan_t },
                    P_hyperbola: { x: sec_t, y: -tan_t },
                    P_sec_axis: { x: sec_t, y: 0 },
                    cos_t: cos_t, sin_t: sin_t
                };

                // Update SVG elements' visibility and position
                const lineOpacity = isAtAsymptote ? 0 : 1;
                dom.svgElements.lines.tanSinh.style.opacity = lineOpacity;
                dom.svgElements.lines.secCosh.style.opacity = lineOpacity;
                dom.svgElements.guidelines.radial.style.opacity = lineOpacity;
                dom.svgElements.guidelines.hyperbola.style.opacity = lineOpacity;
                dom.svgElements.points.hyperbola.style.opacity = lineOpacity;
                dom.svgElements.points.tangent.style.opacity = lineOpacity;
                dom.svgElements.areaHyperbolic.style.opacity = isAtAsymptote ? 0 : 0.2;

                setAttr(dom.svgElements.points.circle, { cx: points.P_circle.x, cy: points.P_circle.y });
                setAttr(dom.svgElements.points.tangent, { cx: points.P_tan_tip.x, cy: points.P_tan_tip.y });
                setAttr(dom.svgElements.points.hyperbola, { cx: points.P_hyperbola.x, cy: points.P_hyperbola.y });

                setAttr(dom.svgElements.lines.cos, { x1: 0, y1: 0, x2: points.P_cos_axis.x, y2: 0 });
                setAttr(dom.svgElements.lines.sin, { x1: points.P_cos_axis.x, y1: 0, x2: points.P_circle.x, y2: points.P_circle.y });
                if (isFinite(sec_t) && isFinite(tan_t)) {
                    setAttr(dom.svgElements.lines.tanSinh, { x1: points.P_sec_axis.x, y1: 0, x2: points.P_hyperbola.x, y2: points.P_hyperbola.y });
                    setAttr(dom.svgElements.lines.secCosh, { x1: 0, y1: 0, x2: points.P_sec_axis.x, y2: 0 });
                    setAttr(dom.svgElements.guidelines.radial, { x1: 0, y1: 0, x2: points.P_hyperbola.x, y2: points.P_hyperbola.y });
                    setAttr(dom.svgElements.guidelines.hyperbola, { x1: points.P_tan_tip.x, y1: points.P_tan_tip.y, x2: points.P_hyperbola.x, y2: points.P_hyperbola.y });
                }
                setAttr(dom.svgElements.guidelines.tan, { x1: points.P_circle.x, y1: points.P_circle.y, x2: points.P_tan_tip.x, y2: points.P_tan_tip.y });

                const sweepFlag = angleDegrees >= 0 ? 0 : 1;
                dom.svgElements.arcTheta.setAttribute('d', `M 1 0 A 1 1 0 0 ${sweepFlag} ${points.P_circle.x} ${points.P_circle.y} L 0 0 Z`);

                if (isFinite(p)) {
                    let hyperbolicAreaPath = 'M 0 0 L 1 0 ';
                    const steps = 60;
                    for (let i = 1; i <= steps; i++) {
                        const p_step = p * (i / steps);
                        hyperbolicAreaPath += `L ${Math.cosh(p_step)} ${-Math.sinh(p_step)} `;
                    }
                    hyperbolicAreaPath += 'Z';
                    dom.svgElements.areaHyperbolic.setAttribute('d', hyperbolicAreaPath);
                }

                updateLabelPositions(theta, points);
            }

