                                    <dt data-highlight="tan-sinh-group">tan(θ) =</dt>
                                    <dd id="val-tan" class="dd-tan connection-highlight green" data-highlight="tan-sinh-group">1.000</dd>
                                    <dt data-highlight="cos-group">cos(θ) =</dt>
                                    <dd id="val-cos" class="dd-cos" data-highlight="cos-group">0.707</dd>
                                    <dt data-highlight="sin-group">sin(θ) =</dt>
                                    <dd id="val-sin" class="dd-sin" data-highlight="sin-group">0.707</dd>
                                </dl>
                            </div>
                            <div class="data-group hyperbolic-element">
                                <h3>Hyperbolic Functions</h3>
                                <dl class="data-list">
                                    <dt data-highlight="sec-cosh-group">cosh(p) =</dt>
                                    <dd id="val-cosh" class="dd-cosh connection-highlight" data-highlight="sec-cosh-group">1.414</dd>
                                    <dt data-highlight="tan-sinh-group">sinh(p) =</dt>
                                    <dd id="val-sinh" class="dd-sinh connection-highlight green" data-highlight="tan-sinh-group">1.000</dd>
                                    <dt data-highlight="p-group">p =</dt>
                                    <dd id="val-p" class="dd-p" data-highlight="p-group">0.881</dd>
                                </dl>
                            </div>
                             <div class="data-group">
                                <h3>Sector Areas</h3>
