        input[type="range"]:hover::-webkit-slider-thumb, input[type="range"]:focus-visible::-webkit-slider-thumb { background: var(--accent-color); }
        input[type="range"]:hover::-moz-range-thumb, input[type="range"]:focus-visible::-moz-range-thumb { background: var(--accent-color); }
        input[type="range"]:focus-visible { outline: 2px solid var(--accent-color); outline-offset: 2px; }

        .play-pause-btn {
            background-color: var(--primary-color); color: var(--bg-color);
            border: none; padding: 0.5rem 1rem;
