        .focus-mode-toggle button.active {
            background-color: var(--primary-color);
            color: var(--bg-color);
            font-weight: 700;
        }
        .focus-mode-toggle button:focus-visible { z-index: 1; outline: 2px solid var(--accent-color); outline-offset: -2px; }

        .data-panel .data-grid {
            display: grid;
            grid-template-columns: 1fr;
            gap: 1.25rem;
            margin-top: 1.25rem;
        }

        .data-group { transition: opacity 0.3s ease; }
        .data-group h3 {
            font-size: 1rem; font-weight: 600;
            color: var(--text-color); margin-bottom: 0.5rem;
        }

        .data-list {
            display: grid; grid-template-columns: auto 1fr;
            gap: 0.5rem 1rem; font-family: var(--font-mono);
            font-size: 0.95rem;
        }

        .data-list dt, .data-list dd {
            padding: 0.2rem 0.4rem;
            border-radius: 4px;
            transition: background-color 0.2s ease;
        }

        .data-list dt { color: var(--text-muted-color); text-align: right; white-space: nowrap; }
        .data-list dd { font-weight: 500; color: var(--text-color); overflow-wrap: break-word; }

        .dd-cos { color: var(--primary-color); }
        .dd-sin { color: var(--secondary-color); }
        .dd-sec, .dd-cosh { color: var(--red-color); }
        .dd-tan, .dd-sinh { color: var(--green-color); }
        .dd-p { color: var(--accent-color); }
        .dd-area-circ { color: var(--primary-color); }
        .dd-area-hyper { color: var(--accent-color); }

        .connection-highlight {
            font-weight: 700;
            background-color: color-mix(in srgb, var(--red-color) 20%, transparent);
            padding: 0.1rem 0.4rem; border-radius: 4px;
        }
        .connection-highlight.green { background-color: color-mix(in srgb, var(--green-color) 20%, transparent); }

        .explanation-panel summary {
            cursor: pointer; font-weight: 600; list-style-position: inside;
            color: var(--secondary-color); font-size: 1.25rem;
        }
        .explanation-panel summary:hover { color: var(--accent-color); }
        .explanation-panel[open] summary { margin-bottom: 1rem; }
        .explanation-panel p { margin-bottom: 1em; color: var(--text-muted-color); }
        .explanation-panel p:last-child { margin-bottom: 0; }
        .explanation-panel code {
            font-family: var(--font-mono); font-size: 0.9em;
            background-color: var(--bg-color); padding: 0.2rem 0.4rem;
            border-radius: 4px; color: var(--accent-color);
        }

        /* --- SVG Styles --- */
        .svg-bg { fill: none; }
        .svg-grid-line { stroke: var(--border-color); stroke-width: 0.01; }
        .svg-axis { stroke: var(--text-muted-color); stroke-width: 0.02; }
        .svg-unit-circle, .svg-unit-hyperbola { stroke-width: 0.03; fill: none; opacity: var(--asymptote-opacity, 0.5); transition: opacity 0.3s ease; }
        .svg-unit-circle { stroke: var(--primary-color); }
        .svg-unit-hyperbola { stroke: var(--secondary-color); stroke-dasharray: 0.1, 0.1; }

        .svg-guideline { stroke: var(--text-muted-color); stroke-width: 0.015; stroke-dasharray: 0.05, 0.05; transition: opacity 0.3s ease-out; }
        .svg-arc-theta { fill: var(--primary-color); opacity: 0.2; transition: opacity 0.3s ease; }
        .svg-area-hyperbolic { fill: var(--accent-color); opacity: var(--asymptote-opacity, 0.2); transition: opacity 0.3s ease; }

        .svg-dynamic-line { transition: opacity 0.2s ease-out; }
        .svg-dynamic-line.circular-element, .svg-dynamic-line.hyperbolic-element { opacity: var(--asymptote-opacity, 1); }

        .svg-point { fill: var(--accent-color); stroke: var(--bg-color); stroke-width: 0.03; r: 0.06; transition: fill 0.2s ease, opacity 0.3s ease; }
        .svg-point.hyperbolic-element { opacity: var(--asymptote-opacity, 1); }
        #svg-point-circle { cursor: grab; }
        #svg-point-circle:active { cursor: grabbing; }

        .svg-label, .svg-axis-label {
            font-family: var(--font-mono); fill: var(--text-color);
            paint-order: stroke; stroke: var(--surface-color); stroke-width: 0.04em;
            stroke-linejoin: round; pointer-events: none; transition: opacity 0.3s ease, transform 0.2s ease;
        }
        .svg-label { font-size: 0.15px; }
        .svg-axis-label { font-size: 0.12px; fill: var(--text-muted-color); }

        .svg-label-cos { fill: var(--primary-color); }
        .svg-label-sin { fill: var(--secondary-color); }
        .svg-label-sec-cosh { fill: var(--red-color); opacity: var(--asymptote-opacity, 1); }
        .svg-label-tan-sinh { fill: var(--green-color); opacity: var(--asymptote-opacity, 1); }
        .svg-label-theta { fill: var(--primary-color); }

        .svg-projection-line {
            stroke: var(--accent-color); stroke-width: 0.03;
            stroke-dasharray: 0; stroke-dashoffset: 0;
            transition: stroke-dashoffset 0.5s ease-in-out, opacity 0.3s;
            opacity: 0; pointer-events: none;
        }
        .svg-projection-line.visible { opacity: 0.8; }

        @keyframes pulse-highlight-svg { 50% { stroke-width: 0.07; } }
        .is-highlighted-svg { animation: pulse-highlight-svg 0.3s ease-out; stroke-width: 0.07; }
        #svg-arc-theta.is-highlighted-svg, #svg-area-hyperbolic.is-highlighted-svg {
            stroke-width: initial; /* Reset stroke animation for filled areas */
            opacity: 0.45; /* Use opacity for highlight instead */
        }

        .is-highlighted-data { background-color: var(--highlight-bg); }

        @keyframes pulse-dynamic-svg { 0% { stroke-width: 0.03; } 50% { stroke-width: 0.08; } 100% { stroke-width: 0.03; } }
        @keyframes pulse-dynamic-data { 0%, 100% { transform: scale(1); } 50% { transform: scale(1.05); } }
        .pulse-anim-svg { animation: pulse-dynamic-svg 0.4s ease-in-out; }
        .pulse-anim-data { animation: pulse-dynamic-data 0.4s ease-in-out; display: inline-block; }

        /* Focus Mode Styles - No !important needed after refactor */
        body.focus-circular .hyperbolic-element,
        body.focus-hyperbolic .circular-element { 
            opacity: 0;
            pointer-events: none;
            transition: opacity 0.3s ease;
        }

        footer {
            text-align: center; padding: 1.5rem; margin-top: 2rem;
            border-top: 1px solid var(--border-color);
            color: var(--text-muted-color); font-size: 0.9rem;
        }

        @media (max-width: 1024px) { .layout-grid { grid-template-columns: 1fr; } }

        @media (min-width: 768px) { .data-panel .data-grid { grid-template-columns: 1fr 1fr; gap: 1.25rem 2rem; } }

