            transition: background-color var(--transition-fast), transform var(--transition-fast);
            display: flex; align-items: center; justify-content: center; gap: 0.5rem;
        }
        .play-pause-btn:hover { background-color: var(--accent-color); }
        .play-pause-btn:active { transform: scale(0.98); }
        .play-pause-btn:focus-visible { outline: 2px solid var(--accent-color); outline-offset: 2px; }

        .focus-mode-toggle {
            display: grid;
            grid-template-columns: 1fr 1fr 1fr;
            border: 1px solid var(--border-color);
            border-radius: 6px;
            overflow: hidden;
        }
        .focus-mode-toggle button {
            background: transparent; border: none; color: var(--text-muted-color);
            padding: 0.4rem; font-size: 0.9rem; font-weight: 500; cursor: pointer;
            transition: background-color var(--transition-fast), color var(--transition-fast);
            border-left: 1px solid var(--border-color);
