        .focus-mode-toggle button:first-child { border-left: none; }
        .focus-mode-toggle button:hover { background-color: color-mix(in srgb, var(--bg-color) 50%, transparent); }
        .focus-mode-toggle button.active {
            background-color: var(--primary-color);
            color: var(--bg-color);
            font-weight: 700;
        }
        .focus-mode-toggle button:focus-visible { z-index: 1; outline: 2px solid var(--accent-color); outline-offset: -2px; }

        input[type="checkbox"][role="switch"] {
            -webkit-appearance: none; appearance: none;
            position: relative;
            width: 48px; height: 26px;
            background-color: var(--border-color);
            border-radius: 13px;
            cursor: pointer;
            flex-shrink: 0;
            transition: background-color var(--transition-fast);
        }
        input[type="checkbox"][role="switch"]::after {
            content: '';
            position: absolute;
            top: 3px; left: 3px;
            width: 20px; height: 20px;
            background-color: var(--surface-color);
            border-radius: 50%;
            transition: transform var(--transition-fast);
        }
        input[type="checkbox"][role="switch"]:checked {
            background-color: var(--primary-color);
        }
        input[type="checkbox"][role="switch"]:checked::after {
