        input[type="checkbox"][role="switch"]:focus-visible {
            outline: 2px solid var(--accent-color);
            outline-offset: 2px;
        }

        .data-panel .data-grid {
            display: grid;
            grid-template-columns: 1fr;
            gap: 1.25rem;
            margin-top: 1.25rem;
