        }
        .svg-label { font-size: 0.15px; }
        .svg-axis-label { font-size: 0.12px; fill: var(--text-muted-color); }

        .svg-label-cos { fill: var(--primary-color); }
        .svg-label-sin { fill: var(--secondary-color); }
        .svg-label-sec-cosh { fill: var(--red-color); opacity: var(--asymptote-opacity, 1); }
        .svg-label-tan-sinh { fill: var(--green-color); opacity: var(--asymptote-opacity, 1); }
        .svg-label-theta { fill: var(--primary-color); }

        .svg-projection-line {
            stroke: var(--accent-color); stroke-width: 0.03;
            stroke-dasharray: 0; stroke-dashoffset: 0;
